/**
 * SPLICE Website E2E Tests - Pricing Page
 * Tests for tier display, overage rates, and Stripe integration
 */

const fs = require("fs");
const path = require("path");

let passed = 0;
let failed = 0;

function test(name, fn) {
  try {
    fn();
    console.log(`  ✓ ${name}`);
    passed++;
  } catch (error) {
    console.log(`  ✗ ${name}`);
    console.log(`    Error: ${error.message}`);
    failed++;
  }
}

function assert(condition, message) {
  if (!condition) {
    throw new Error(message || "Assertion failed");
  }
}

const pricingPage = fs.readFileSync(
  path.join(__dirname, "..", "src", "app", "pricing", "page.tsx"),
  "utf8"
);

console.log("\n=== Pricing Page E2E Tests ===\n");

// ============================================
// Tier Tests
// ============================================
console.log("Pricing Tiers:");

test("Has all 3 pricing tiers", () => {
  assert(pricingPage.includes('name: "Starter"'), "Missing Starter tier");
  assert(pricingPage.includes('name: "Pro"'), "Missing Pro tier");
  assert(pricingPage.includes('name: "Team"'), "Missing Team tier");
});

test("Tiers have correct prices", () => {
  assert(pricingPage.includes('price: "$15"'), "Starter should be $15");
  assert(pricingPage.includes('price: "$39"'), "Pro should be $39");
  assert(pricingPage.includes('price: "$129"'), "Team should be $129");
});

test("Tiers have processing hours", () => {
  assert(pricingPage.includes("12 hours"), "Starter should have 12 hours");
  assert(pricingPage.includes("30 hours"), "Pro should have 30 hours");
  assert(pricingPage.includes("100 hours"), "Team should have 100 hours");
});

test("Pro tier is marked as popular", () => {
  const proTier = pricingPage.match(/name:\s*"Pro"[\s\S]*?popular:\s*(true|false)/);
  assert(proTier && proTier[1] === "true", "Pro tier should be popular");
});

test("Tiers have music credits", () => {
  assert(pricingPage.includes("5 AI music"), "Starter should have 5 music credits");
  assert(pricingPage.includes("20 AI music"), "Pro should have 20 music credits");
  assert(pricingPage.includes("100 AI music"), "Team should have 100 music credits");
});

test("Tiers have vocal isolation info", () => {
  assert(pricingPage.includes("45 min vocal isolation"), "Pro should have 45 min");
  assert(pricingPage.includes("3 hours vocal isolation"), "Team should have 3 hours");
});

// ============================================
// Overage Tests
// ============================================
console.log("\nOverage Rates:");

test("Shows processing overage rate", () => {
  assert(pricingPage.includes("$1.00/hour"), "Missing processing overage rate");
});

test("Shows vocal isolation overage rate", () => {
  assert(pricingPage.includes("$0.10/minute"), "Missing isolation overage rate");
});

test("Shows music generation overage rates", () => {
  assert(pricingPage.includes("$0.50/song"), "Missing Starter music overage");
  assert(pricingPage.includes("$0.30/song"), "Missing Pro music overage");
  assert(pricingPage.includes("$0.20/song"), "Missing Team music overage");
});

// ============================================
// Stripe Integration Tests
// ============================================
console.log("\nStripe Integration:");

test("Has Stripe price IDs", () => {
  assert(pricingPage.includes("price_"), "Missing Stripe price IDs");
});

test("Has checkout links", () => {
  assert(pricingPage.includes("/checkout?plan="), "Missing checkout links");
});

// ============================================
// FAQ Tests
// ============================================
console.log("\nPricing FAQ:");

test("Has processing hours FAQ", () => {
  assert(
    pricingPage.includes("processing hour") || pricingPage.includes("processing hour"),
    "Missing processing hours FAQ"
  );
});

test("Has plan switching FAQ", () => {
  assert(
    pricingPage.includes("switch plan") || pricingPage.includes("upgrade"),
    "Missing plan switching FAQ"
  );
});

test("Has overage FAQ", () => {
  assert(
    pricingPage.includes("exceed") || pricingPage.includes("limit"),
    "Missing overage FAQ"
  );
});

test("Has trial FAQ", () => {
  assert(pricingPage.includes("free tier") || pricingPage.includes("free trial"), "Missing trial FAQ");
});

// ============================================
// CTA Tests
// ============================================
console.log("\nCTAs:");

test("Has Start Free Trial buttons", () => {
  assert(pricingPage.includes("Start Free Trial"), "Missing trial CTAs");
});

test("Has Contact Sales for Team tier", () => {
  assert(pricingPage.includes("Contact Sales"), "Missing Contact Sales CTA");
});

// ============================================
// Results
// ============================================
console.log("\n========================================");
console.log(`Pricing Page Tests: ${passed} passed, ${failed} failed`);
console.log("========================================\n");

process.exit(failed > 0 ? 1 : 0);

"use client";

import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { motion } from "framer-motion";
import { Check, Sparkles, Clock, Users, TrendingUp } from "lucide-react";
import Link from "next/link";
import { useState, useEffect } from "react";

const plans = [
  {
    name: "Starter",
    price: "$15",
    originalPrice: "$25",
    period: "/month",
    annualPrice: "$12",
    savings: "Save 40%",
    description: "Perfect for individual creators getting started",
    features: [
      "4 hours of processing/month",
      "AI silence removal",
      "Smart take detection",
      "2 AI music generations/month",
      "Basic caption templates",
      "Email support",
    ],
    overageNote: "$2/hr extra, $1/song extra",
    cta: "Start Free Trial",
    popular: false,
    spotsLeft: 47,
    priceId: "price_1SinNn2L5BseYEbCZmDWuWnk",
    annualPriceId: "price_1SinNo2L5BseYEbChvWjXVoS",
  },
  {
    name: "Pro",
    price: "$39",
    originalPrice: "$65",
    period: "/month",
    annualPrice: "$31",
    savings: "Save 40%",
    description: "For serious creators who need more power",
    features: [
      "15 hours of processing/month",
      "Everything in Starter, plus:",
      "45 min vocal isolation included",
      "10 AI music generations/month",
      "All 5 caption templates",
      "Social reframe with face tracking",
      "Text-based editing",
      "Priority support",
    ],
    overageNote: "$2/hr extra, $1/song extra",
    cta: "Start Free Trial",
    popular: true,
    spotsLeft: 23,
    priceId: "price_1SinOC2L5BseYEbCP4IbcZuu",
    annualPriceId: "price_1SinOD2L5BseYEbCaJSROCGG",
  },
  {
    name: "Team",
    price: "$129",
    originalPrice: "$215",
    period: "/month",
    annualPrice: "$103",
    savings: "Save 40%",
    description: "For teams and high-volume creators",
    features: [
      "50 hours of processing/month",
      "Everything in Pro, plus:",
      "3 hours vocal isolation included",
      "50 AI music generations/month",
      "Multitrack auto-balance",
      "Chapter detection with YouTube export",
      "Profanity bleeping (5 types)",
      "Dedicated account manager",
    ],
    overageNote: "$2/hr extra, $1/song extra",
    cta: "Contact Sales",
    popular: false,
    spotsLeft: 8,
    priceId: "price_1SinOe2L5BseYEbCK3A8doSn",
    annualPriceId: "price_1SinOg2L5BseYEbCcbIG4ofY",
  },
];

// Countdown timer component
function UrgencyCountdown() {
  const [timeLeft, setTimeLeft] = useState({ days: 0, hours: 0, minutes: 0, seconds: 0 });

  useEffect(() => {
    // Set end date to end of current month
    const getEndDate = () => {
      const now = new Date();
      return new Date(now.getFullYear(), now.getMonth() + 1, 0, 23, 59, 59);
    };

    const calculateTimeLeft = () => {
      const difference = getEndDate().getTime() - new Date().getTime();
      if (difference > 0) {
        return {
          days: Math.floor(difference / (1000 * 60 * 60 * 24)),
          hours: Math.floor((difference / (1000 * 60 * 60)) % 24),
          minutes: Math.floor((difference / 1000 / 60) % 60),
          seconds: Math.floor((difference / 1000) % 60),
        };
      }
      return { days: 0, hours: 0, minutes: 0, seconds: 0 };
    };

    setTimeLeft(calculateTimeLeft());
    const timer = setInterval(() => setTimeLeft(calculateTimeLeft()), 1000);
    return () => clearInterval(timer);
  }, []);

  return (
    <div className="flex items-center justify-center gap-3 text-sm">
      {[
        { value: timeLeft.days, label: "Days" },
        { value: timeLeft.hours, label: "Hrs" },
        { value: timeLeft.minutes, label: "Min" },
        { value: timeLeft.seconds, label: "Sec" },
      ].map((item, i) => (
        <div key={item.label} className="flex items-center gap-1">
          <div className="bg-[#00D4FF]/20 text-[#00D4FF] font-mono font-bold px-2 py-1 rounded">
            {String(item.value).padStart(2, "0")}
          </div>
          <span className="text-gray-400 text-xs">{item.label}</span>
          {i < 3 && <span className="text-gray-600 ml-1">:</span>}
        </div>
      ))}
    </div>
  );
}

export function Pricing() {
  return (
    <section id="pricing" className="py-24 bg-[#060b14] dark">
      <div className="container mx-auto px-4">
        {/* Section header */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.5 }}
          className="text-center mb-12"
        >
          {/* Urgency banner */}
          <div className="inline-flex items-center gap-3 mb-4 px-4 py-2 rounded-full bg-gradient-to-r from-orange-500/20 to-red-500/20 border border-orange-500/30">
            <span className="flex h-2 w-2">
              <span className="animate-ping absolute inline-flex h-2 w-2 rounded-full bg-orange-400 opacity-75"></span>
              <span className="relative inline-flex rounded-full h-2 w-2 bg-orange-500"></span>
            </span>
            <span className="text-sm font-medium text-orange-400">
              Beta Pricing Ends Soon — Lock in 40% savings forever
            </span>
          </div>

          <Badge
            variant="outline"
            className="mb-4 px-4 py-1.5 text-sm font-medium border-[#00D4FF]/30 bg-[#00D4FF]/10 text-[#00D4FF]"
          >
            <Sparkles className="w-3.5 h-3.5 mr-1.5" />
            14-day free trial on all plans
          </Badge>
          <h2 className="text-3xl md:text-4xl font-bold mb-4 text-white">
            Simple, Transparent{" "}
            <span className="gradient-text">Pricing</span>
          </h2>
          <p className="text-lg text-gray-400 max-w-2xl mx-auto mb-6">
            No hidden fees. No credit card required for trial. Cancel anytime.
          </p>

          {/* Countdown timer */}
          <div className="mb-8">
            <p className="text-sm text-gray-500 mb-2">Beta pricing expires in:</p>
            <UrgencyCountdown />
          </div>

          {/* Social proof */}
          <div className="flex flex-wrap items-center justify-center gap-6 text-sm text-gray-400">
            <div className="flex items-center gap-2">
              <Users className="w-4 h-4 text-[#00D4FF]" />
              <span><span className="text-white font-semibold">2,847</span> editors joined this week</span>
            </div>
            <div className="flex items-center gap-2">
              <TrendingUp className="w-4 h-4 text-green-400" />
              <span><span className="text-white font-semibold">94%</span> of users upgrade after trial</span>
            </div>
          </div>
        </motion.div>

        {/* Pricing cards */}
        <div className="grid md:grid-cols-3 gap-8 max-w-6xl mx-auto">
          {plans.map((plan, index) => (
            <motion.div
              key={plan.name}
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.5, delay: index * 0.1 }}
              className={plan.popular ? "md:-mt-4 md:mb-4" : ""}
            >
              <Card
                className={`h-full relative bg-[#0d1420] ${
                  plan.popular
                    ? "border-[#00D4FF] shadow-lg shadow-[#00D4FF]/20"
                    : "border-gray-800"
                }`}
              >
                {plan.popular && (
                  <div className="absolute -top-3 left-1/2 -translate-x-1/2">
                    <Badge className="bg-[#00D4FF] text-black font-semibold">
                      Most Popular
                    </Badge>
                  </div>
                )}

                <CardHeader className="text-center pb-2 pt-8">
                  <div className="flex items-center justify-center gap-2 mb-1">
                    <h3 className="text-xl font-bold text-white">{plan.name}</h3>
                    <Badge className="bg-green-500/20 text-green-400 border-green-500/30 text-xs">
                      {plan.savings}
                    </Badge>
                  </div>
                  <div className="mt-4">
                    <span className="text-lg text-gray-500 line-through mr-2">{plan.originalPrice}</span>
                    <span className="text-4xl font-bold text-white">{plan.price}</span>
                    <span className="text-gray-400">{plan.period}</span>
                  </div>
                  <p className="text-sm text-gray-400 mt-2">
                    {plan.description}
                  </p>
                  {/* Spots remaining urgency */}
                  <div className="mt-3 flex items-center justify-center gap-1.5 text-xs">
                    <span className="flex h-1.5 w-1.5">
                      <span className="animate-ping absolute inline-flex h-1.5 w-1.5 rounded-full bg-orange-400 opacity-75"></span>
                      <span className="relative inline-flex rounded-full h-1.5 w-1.5 bg-orange-500"></span>
                    </span>
                    <span className="text-orange-400">
                      Only <span className="font-bold">{plan.spotsLeft}</span> beta spots left at this price
                    </span>
                  </div>
                </CardHeader>

                <CardContent className="pt-4">
                  <ul className="space-y-3 mb-8">
                    {plan.features.map((feature) => (
                      <li key={feature} className="flex items-start gap-2">
                        <Check className="w-5 h-5 text-[#00D4FF] shrink-0 mt-0.5" />
                        <span className="text-sm text-gray-300">{feature}</span>
                      </li>
                    ))}
                  </ul>

                  <Button
                    className={`w-full ${
                      plan.popular
                        ? "bg-[#00D4FF] hover:bg-[#00B8E6] text-black font-semibold"
                        : "bg-transparent border-gray-700 text-white hover:bg-white/10"
                    }`}
                    variant={plan.popular ? "default" : "outline"}
                    size="lg"
                    asChild
                  >
                    <Link href={plan.cta === "Contact Sales" ? "/contact" : `/checkout?plan=${plan.name.toLowerCase()}`}>
                      {plan.cta}
                    </Link>
                  </Button>
                </CardContent>
              </Card>
            </motion.div>
          ))}
        </div>

        {/* Overage pricing note */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.5, delay: 0.4 }}
          className="mt-12 text-center space-y-2"
        >
          <p className="text-sm text-gray-400">
            Need more? Overage pricing: <span className="text-white">$2/hour</span> for processing, <span className="text-white">$1/song</span> for music.
          </p>
          <p className="text-sm text-gray-500">
            Save 20% with annual billing.{" "}
            <Link href="/pricing" className="text-[#00D4FF] hover:underline">
              View detailed pricing
            </Link>
          </p>
        </motion.div>
      </div>
    </section>
  );
}

