}

/* Music Library */
.music-library-list {
    max-height: 200px;
    overflow-y: auto;
}

.music-library-empty {
    text-align: center;
    color: #666;
    padding: 20px;
    font-size: 10px;
}

.music-library-item {
    display: flex;
    align-items: center;
    gap: 8px;
    padding: 8px;
    background: var(--bg-panel);
    border-radius: 4px;
    margin-bottom: 4px;
    cursor: pointer;
    transition: background 0.2s;
}

.music-library-item:hover {
    background: #333;
}

.music-library-item-info {
    flex: 1;
    min-width: 0;
}

.music-library-item-title {
    font-size: 10px;
    color: #e1e1e1;
    font-weight: 500;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}

.music-library-item-meta {
    font-size: 9px;
    color: var(--text-muted);
    display: flex;
    gap: 8px;
}

.music-library-item-actions {
    display: flex;
    gap: 4px;
}

.music-library-item-actions .btn {
    padding: 4px 6px;
    font-size: 9px;
}

/* Music Status */
.music-status {
    font-size: 10px;
    padding: 4px 0;
    min-height: 18px;
}

.music-status-info {
    color: var(--accent-primary);
}

.music-status-success {
    color: #28a745;
}

.music-status-error {
    color: #dc3545;
}

/* Music Progress */
.music-progress {
    margin: 10px 0;
}

.music-progress.hidden {
    display: none;
}

.music-progress-bar-container {
    height: 4px;
    background: #333;
    border-radius: 2px;
    overflow: hidden;
    margin-bottom: 6px;
}

.music-progress-bar {
    height: 100%;
    background: var(--accent-primary);
    transition: width 0.3s ease;
