/**
 * SPLICE CEP Panel Styles
 * Premium "Deep Space" Theme
 */

:root {
    /* Palette */
    --bg-core: #121212;
    --bg-panel: var(--bg-panel);
    --bg-card: rgba(30, 30, 30, 0.6);
    --glass-border: rgba(255, 255, 255, 0.08);
    --glass-highlight: rgba(255, 255, 255, 0.03);

    /* Accents */
    --accent-primary: var(--accent-primary);
    --accent-gradient: linear-gradient(135deg, var(--accent-primary), #a64dff);
    --accent-glow: 0 0 20px rgba(74, 158, 255, 0.15);

    /* Functional Colors */
    --text-main: #f0f0f0;
    --text-muted: var(--text-muted)888;
    --success: #00e676;
    --error: #ff5252;

    /* Spacing & Radius */
    --radius-sm: 6px;
    --radius-md: 12px;
    --radius-lg: 16px;
    --spacing-xs: 4px;
    --spacing-sm: 8px;
    --spacing-md: 16px;

    /* Transitions */
    --ease-smooth: cubic-bezier(0.4, 0.0, 0.2, 1);
}

/* Reset & Base */
* {
    box-sizing: border-box;
    margin: 0;
    padding: 0;
}

body {
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif;
    background: var(--bg-core);
    color: var(--text-main);
    margin: 0;
    padding: var(--spacing-md);
    height: 100vh;
    overflow-y: auto;
    overflow-x: hidden;
    font-size: 12px;
    line-height: 1.5;
    -webkit-font-smoothing: antialiased;
}

/* Loading Overlay */
#loading-overlay {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: var(--bg-core);
    z-index: 9999;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    transition: opacity 0.5s var(--ease-smooth), visibility 0.5s;
}

#loading-overlay.hidden {
    opacity: 0;
    visibility: hidden;
    pointer-events: none;
}

.loader-logo {
    width: 48px;
    height: 48px;
    border-radius: 50%;
    background: var(--accent-gradient);
    animation: pulse-ring 2s infinite;
    margin-bottom: var(--spacing-md);
    box-shadow: var(--accent-glow);
}

.loader-text {
    font-size: 11px;
    color: var(--text-muted);
    letter-spacing: 1px;
    text-transform: uppercase;
    font-weight: 500;
}

@keyframes pulse-ring {
    0% {
        transform: scale(0.95);
        box-shadow: 0 0 0 0 rgba(74, 158, 255, 0.7);
    }

    70% {
        transform: scale(1);
        box-shadow: 0 0 0 10px rgba(74, 158, 255, 0);
    }

    100% {
        transform: scale(0.95);
        box-shadow: 0 0 0 0 rgba(74, 158, 255, 0);
    }
}

/* Scrollbar */
::-webkit-scrollbar {
    width: 6px;
}

::-webkit-scrollbar-track {
    background: transparent;
}

::-webkit-scrollbar-thumb {
    background: rgba(255, 255, 255, 0.1);
    border-radius: 3px;
}

::-webkit-scrollbar-thumb:hover {
    background: rgba(255, 255, 255, 0.2);
}

/* Base UI Components */
button {
    font-family: inherit;
}

/* Header */
.header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: var(--spacing-md);
    padding-bottom: var(--spacing-sm);
    border-bottom: 1px solid var(--glass-border);
}

.header h1 {
    font-size: 16px;
    font-weight: 700;
    margin: 0;
    background: var(--accent-gradient);
    -webkit-background-clip: text;
    background-clip: text;
    -webkit-text-fill-color: transparent;
    letter-spacing: -0.5px;
}

.header-actions {
    display: flex;
    gap: var(--spacing-sm);
    align-items: center;
}

/* Action Card */
.action-card {
    background: var(--bg-card);
    backdrop-filter: blur(10px);
    border: 1px solid var(--glass-border);
    border-radius: var(--radius-md);
    padding: var(--spacing-md);
    margin-bottom: var(--spacing-md);
    box-shadow: 0 4px 20px rgba(0, 0, 0, 0.2);
    transition: transform 0.2s var(--ease-smooth);
}

.action-card h2 {
    font-size: 10px;
    font-weight: 700;
    margin: 0 0 var(--spacing-md) 0;
    color: var(--text-muted);
    text-transform: uppercase;
    letter-spacing: 1px;
}

/* GO Button */
.go-btn {
    width: 100%;
    padding: 16px;
    font-size: 14px;
    font-weight: 600;
    background: var(--accent-gradient);
    border: none;
    border-radius: var(--radius-md);
    color: white;
    cursor: pointer;
    margin-bottom: var(--spacing-sm);
    letter-spacing: 0.5px;
    transition: transform 0.2s var(--ease-smooth), box-shadow 0.2s;
    box-shadow: var(--accent-glow);
}

.go-btn:hover {
    transform: translateY(-2px);
    box-shadow: 0 4px 15px rgba(74, 158, 255, 0.3);
}

.go-btn:active {
    transform: translateY(0);
}

.go-btn:disabled {
    opacity: 0.5;
    cursor: not-allowed;
    transform: none;
    box-shadow: none;
}

/* Icon Buttons */
.icon-btn {
    background: transparent;
    border: 1px solid var(--glass-border);
    color: var(--text-muted);
    width: 32px;
    height: 32px;
    border-radius: var(--radius-sm);
    cursor: pointer;
    font-size: 14px;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: all 0.2s var(--ease-smooth);
}

.icon-btn:hover {
    background: var(--glass-highlight);
    color: var(--text-main);
    border-color: var(--text-muted);
}

/* Options Toggle */
.options-toggle {
    background: transparent;
    border: none;
    color: var(--text-muted);
    font-size: 10px;
    cursor: pointer;
    padding: 4px 0;
    width: 100%;
    text-align: left;
}

.options-toggle:hover {
    color: #aaa;
}

.options-toggle::before {
    content: '+';
    margin-right: 4px;
    font-weight: bold;
}

.options-toggle.expanded::before {
    content: '-';
}

/* Options Panel */
.options-panel {
    margin-top: 8px;
    padding-top: 8px;
    border-top: 1px solid var(--glass-border);
}

.options-panel.collapsed,
.sub-options.collapsed {
    display: none;
}

/* Form Elements */
.form-group {
    margin-bottom: 10px;
}

.form-group label {
    display: block;
    font-size: 10px;
    color: var(--text-muted);
    margin-bottom: 4px;
}

.form-group.small {
    margin-bottom: 6px;
}

.form-group.small label {
    font-size: 9px;
}

input[type="text"],
input[type="email"],
input[type="number"],
select {
    width: 100%;
    padding: 6px 8px;
    background: var(--bg-panel);
    border: 1px solid var(--glass-border);
    border-radius: 4px;
    color: #e1e1e1;
    font-size: 11px;
}

input[type="text"]:focus,
input[type="email"]:focus,
input[type="number"]:focus,
select:focus {
    outline: none;
    border-color: var(--accent-primary);
}

/* Slider */
.slider-container {
    margin-bottom: var(--spacing-md);
}

.slider-container label {
    display: block;
    font-size: 11px;
    color: var(--text-muted);
    margin-bottom: var(--spacing-xs);
    font-weight: 500;
}

.slider-container.small {
    margin-bottom: var(--spacing-sm);
}

input[type="range"] {
    width: 100%;
    height: 4px;
    background: rgba(255, 255, 255, 0.1);
    border-radius: 2px;
    outline: none;
    -webkit-appearance: none;
    appearance: none;
    /* Fixed lint: standard property */
    cursor: pointer;
}

input[type="range"]::-webkit-slider-thumb {
    -webkit-appearance: none;
    appearance: none;
    /* Fixed lint */
    width: 16px;
    height: 16px;
    background: var(--accent-primary);
    border-radius: 50%;
    cursor: pointer;
    box-shadow: 0 0 10px rgba(74, 158, 255, 0.4);
    transition: transform 0.2s var(--ease-smooth);
    margin-top: -6px;
    /* Vertical centering adjustment */
}

input[type="range"]::-webkit-slider-thumb:hover {
    transform: scale(1.2);
}

input[type="range"]::-webkit-slider-runnable-track {
    width: 100%;
    height: 4px;
    cursor: pointer;
    background: rgba(255, 255, 255, 0.1);
    border-radius: 2px;
}

.slider-labels {
    display: flex;
    justify-content: space-between;
    font-size: 10px;
    color: var(--text-muted);
    margin-top: var(--spacing-xs);
}

/* Checkbox */
.checkbox-label {
    display: flex;
    align-items: center;
    gap: 8px;
    font-size: 11px;
    color: var(--text-main);
    cursor: pointer;
    margin-bottom: var(--spacing-sm);
    user-select: none;
}

.checkbox-label input[type="checkbox"] {
    width: 16px;
    height: 16px;
    accent-color: var(--accent-primary);
    background: rgba(0, 0, 0, 0.3);
    border: 1px solid var(--glass-border);
    border-radius: 4px;
    cursor: pointer;
}

/* Tier Badge */
.tier-badge {
    font-size: 9px;
    background: var(--accent-gradient);
    color: white;
    padding: 2px 6px;
    border-radius: var(--radius-sm);
    margin-left: var(--spacing-xs);
    font-weight: 600;
    box-shadow: var(--accent-glow);
}

/* Feature Toggles */
.feature-toggles {
    margin-top: 10px;
}

.sub-options {
    margin-left: 20px;
    margin-top: 6px;
    padding: 8px;
    background: rgba(0, 0, 0, 0.2);
    border-radius: 4px;
}

/* Results Area */
.results-area {
    background: var(--bg-panel);
    border-radius: 6px;
    padding: 12px;
    min-height: 60px;
}

.results-empty {
    text-align: center;
    color: #666;
    padding: 20px;
    font-size: 11px;
}

/* Progress */
.progress-container {
    text-align: center;
    padding: var(--spacing-md) 0;
}

.progress-container.hidden {
    display: none;
}

.progress-bar-container {
    height: 6px;
    background: rgba(0, 0, 0, 0.3);
    border-radius: 3px;
    overflow: hidden;
    margin-bottom: var(--spacing-sm);
    box-shadow: inset 0 1px 2px rgba(0, 0, 0, 0.3);
}

.progress-bar {
    height: 100%;
    background: var(--accent-gradient);
    transition: width 0.3s var(--ease-smooth);
    box-shadow: var(--accent-glow);
}

.progress-text {
    font-size: 11px;
    color: var(--text-muted);
    font-weight: 500;
}

/* Preview Section */
.preview-section {
    margin-top: 8px;
}

.preview-section.hidden {
    display: none;
}

.preview-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 8px;
}

.preview-header h3 {
    font-size: 11px;
    font-weight: 600;
    color: #aaa;
    text-transform: uppercase;
}

.preview-counts {
    display: flex;
    gap: 10px;
    font-size: 10px;
    color: #666;
}

.preview-counts span span {
    color: var(--accent-primary);
    font-weight: 600;
}

.preview-controls {
    margin-bottom: 8px;
}

.preview-list {
    max-height: 200px;
    overflow-y: auto;
    margin-bottom: var(--spacing-md);
    padding-right: 4px;
    /* Space for scrollbar */
}

.preview-item {
    display: flex;
    align-items: center;
    padding: 8px 12px;
    margin-bottom: 6px;
    background: rgba(30, 30, 30, 0.4);
    border: 1px solid var(--glass-border);
    border-radius: var(--radius-sm);
    border-left: 3px solid var(--accent-primary);
    cursor: pointer;
    transition: all 0.15s var(--ease-smooth);
}

.preview-item:hover {
    background: rgba(255, 255, 255, 0.05);
    transform: translateX(2px);
}

.preview-item.excluded {
    opacity: 0.5;
    border-left-color: var(--text-muted);
    background: transparent;
}

.preview-item.take-item {
    border-left-color: var(--success);
}

.preview-item-check {
    min-width: 16px;
    min-height: 16px;
    margin-right: 10px;
    accent-color: var(--accent-primary);
}

.preview-item-icon {
    margin-right: 10px;
    font-size: 14px;
}

.preview-item-info {
    flex: 1;
    min-width: 0;
}

.preview-item-time {
    font-size: 11px;
    font-weight: 500;
    color: #e1e1e1;
}

.preview-item-duration,
.preview-item-label {
    font-size: 9px;
    color: var(--text-muted);
    margin-top: 2px;
}

.preview-actions {
    display: flex;
    gap: 8px;
}

/* Buttons */
.btn {
    padding: 8px 16px;
    font-size: 12px;
    font-weight: 500;
    border: none;
    border-radius: var(--radius-sm);
    cursor: pointer;
    transition: all 0.2s var(--ease-smooth);
}

.btn-primary {
    background: var(--accent-primary);
    color: white;
    box-shadow: 0 2px 8px rgba(74, 158, 255, 0.2);
}

.btn-primary:hover {
    background: #5aa8ff;
    transform: translateY(-1px);
    box-shadow: 0 4px 12px rgba(74, 158, 255, 0.3);
}

.btn-secondary {
    background: rgba(255, 255, 255, 0.1);
    color: var(--text-main);
}

.btn-secondary:hover {
    background: rgba(255, 255, 255, 0.15);
}

.btn-ghost {
    background: transparent;
    color: var(--text-muted);
    border: 1px solid var(--glass-border);
}

.btn-ghost:hover {
    background: var(--glass-highlight);
    color: var(--text-main);
    border-color: var(--text-muted);
}

.btn-small {
    padding: 6px 10px;
    font-size: 11px;
}

/* Chapter Results */
.chapter-results,
.zoom-results {
    margin-top: 12px;
    padding-top: 12px;
    border-top: 1px solid #333;
}

.chapter-results.hidden,
.zoom-results.hidden {
    display: none;
}

.chapter-header,
.zoom-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 8px;
}

.chapter-header h3,
.zoom-header h3 {
    font-size: 11px;
    font-weight: 600;
    color: #aaa;
    text-transform: uppercase;
}

.chapter-actions {
    display: flex;
    gap: 4px;
}

.chapter-list,
.zoom-list {
    max-height: 120px;
    overflow-y: auto;
}

.chapter-item {
    display: flex;
    gap: 10px;
    padding: 6px 8px;
    background: var(--bg-card);
    border-radius: 4px;
    margin-bottom: 4px;
    cursor: pointer;
    transition: background 0.15s;
}

.chapter-item:hover {
    background: #333;
}

.chapter-time {
    font-size: 10px;
    color: var(--accent-primary);
    font-weight: 500;
    min-width: 50px;
}

.chapter-title {
    font-size: 10px;
    color: #e1e1e1;
    flex: 1;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}

/* Modal */
.modal {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.6);
    backdrop-filter: blur(4px);
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 1000;
    animation: fadeIn 0.2s ease-out;
}

.modal.hidden {
    display: none;
}

.modal-content {
    background: var(--bg-panel);
    /* Solid background for modal content to ensure readability */
    border-radius: var(--radius-lg);
    width: 90%;
    max-width: 360px;
    max-height: 85%;
    overflow-y: auto;
    border: 1px solid var(--glass-border);
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.4);
    animation: slideUp 0.3s var(--ease-smooth);
}

.modal-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: var(--spacing-md);
    border-bottom: 1px solid var(--glass-border);
    background: rgba(255, 255, 255, 0.02);
}

.modal-header h2 {
    font-size: 14px;
    font-weight: 600;
    margin: 0;
    color: var(--text-main);
}

.close-btn {
    background: transparent;
    border: none;
    color: var(--text-muted);
    font-size: 20px;
    cursor: pointer;
    padding: 4px;
    line-height: 1;
    border-radius: 50%;
    transition: all 0.2s;
    width: 28px;
    height: 28px;
    display: flex;
    align-items: center;
    justify-content: center;
}

.close-btn:hover {
    color: var(--text-main);
    background: rgba(255, 255, 255, 0.1);
}

@keyframes fadeIn {
    from {
        opacity: 0;
    }

    to {
        opacity: 1;
    }
}

@keyframes slideUp {
    from {
        transform: translateY(20px);
        opacity: 0;
    }

    to {
        transform: translateY(0);
        opacity: 1;
    }
}

/* Status Bar */
#status {
    padding: 8px 12px;
    background: rgba(0, 0, 0, 0.3);
    border: 1px solid var(--glass-border);
    border-radius: var(--radius-sm);
    font-size: 11px;
    margin-bottom: var(--spacing-md);
    color: var(--text-muted);
    display: flex;
    align-items: center;
}

/* Form Elements */
.form-group {
    margin-bottom: var(--spacing-md);
}

.form-group label {
    display: block;
    font-size: 11px;
    color: var(--text-muted);
    margin-bottom: var(--spacing-xs);
