    padding: 6px 0;
    margin-bottom: 8px;
    border-bottom: 1px solid #333;
}

.toolbar-divider {
    width: 1px;
    height: 18px;
    background: var(--glass-border);
    margin: 0 4px;
}

.btn-icon {
    width: 28px;
    height: 28px;
    padding: 0;
    font-size: 14px;
