   127→/**
   128→ * Make HTTP request to Mureka API
   129→ * @param {string} method - HTTP method
   130→ * @param {string} endpoint - API endpoint
   131→ * @param {Object} data - Request body (for POST)
   132→ * @returns {Promise<Object>} API response
   133→ */
   134→async function murekaRequest(method, endpoint, data = null) {
   135→  if (!MUREKA_API_KEY) {
   136→    throw new Error('Mureka API key not configured. Set MUREKA_API_KEY environment variable.');
   137→  }
   138→
   139→  return new Promise((resolve, reject) => {
   140→    const options = {
   141→      hostname: MUREKA_BASE_URL,
   142→      path: `/${MUREKA_API_VERSION}${endpoint}`,
   143→      method,
   144→      headers: {
   145→        'Authorization': `Bearer ${MUREKA_API_KEY}`,
   146→        'Content-Type': 'application/json',
   147→        'Accept': 'application/json'
   148→      }
   149→    };
   150→
   151→    const req = https.request(options, (res) => {
   152→      let responseData = '';
   153→
   154→      res.on('data', (chunk) => {
   155→        responseData += chunk;
   156→      });
   157→
   158→      res.on('end', () => {
   159→        try {
   160→          const parsed = JSON.parse(responseData);
   161→
   162→          if (res.statusCode >= 400) {
   163→            const error = new Error(parsed.message || parsed.error || 'Mureka API error');
   164→            error.statusCode = res.statusCode;
   165→            error.response = parsed;
   166→            reject(error);
   167→            return;
   168→          }
   169→
   170→          resolve(parsed);
   171→        } catch (parseError) {
   172→          reject(new Error(`Failed to parse Mureka response: ${parseError.message}`));
   173→        }
   174→      });
   175→    });
   176→
   177→    req.on('error', (error) => {
   178→      reject(new Error(`Mureka API request failed: ${error.message}`));
   179→    });
   180→
   181→    req.on('timeout', () => {
   182→      req.destroy();
   183→      reject(new Error('Mureka API request timeout'));
   184→    });
   185→
   186→    req.setTimeout(60000); // 60 second timeout
   187→
   188→    if (data) {
   189→      req.write(JSON.stringify(data));
   190→    }
   191→
   192→    req.end();
   193→  });
   194→}
   195→
   196→/**
   197→ * Submit music generation request to Mureka
   198→ * @param {Object} options - Generation options
   199→ * @returns {Promise<{taskId: string, status: string}>}
   200→ */
   201→async function submitGeneration(options) {
   202→  const prompt = buildMurekaPrompt(options);
   203→  const duration = clampDuration(options.duration || DEFAULT_DURATION);
   204→
   205→  const requestBody = {
   206→    prompt,
   207→    duration,
   208→    type: 'instrumental', // Background music mode
   209→    format: 'wav',
   210→    quality: 'high'
   211→  };
   212→
   213→  // Add reference track if available
   214→  if (options.referenceTrackUrl) {
   215→    requestBody.reference_url = options.referenceTrackUrl;
   216→  }
   217→
   218→  const response = await murekaRequest('POST', '/generate', requestBody);
   219→
   220→  if (!response.task_id) {
   221→    throw new Error('Mureka did not return a task ID');
   222→  }
   223→
   224→  return {
   225→    taskId: response.task_id,
   226→    status: response.status || 'pending',
   227→    estimatedTime: response.estimated_time || '3-5 minutes'
   228→  };
   229→}
   230→
   231→/**
   232→ * Check status of a generation task
   233→ * @param {string} taskId - Mureka task ID
   234→ * @returns {Promise<Object>} Task status
   235→ */
   236→async function checkTaskStatus(taskId) {
   237→  const response = await murekaRequest('GET', `/tasks/${taskId}`);
   238→
   239→  return {
   240→    taskId,
   241→    status: response.status,
   242→    progress: response.progress || 0,
   243→    audioUrl: response.audio_url || null,
   244→    previewUrl: response.preview_url || null,
   245→    duration: response.duration || null,
   246→    error: response.error || null
   247→  };
   248→}
   249→
   250→/**
   251→ * Poll for task completion
   252→ * @param {string} taskId - Mureka task ID
   253→ * @param {Function} onProgress - Progress callback
   254→ * @returns {Promise<Object>} Completed task result
   255→ */
   256→async function pollForCompletion(taskId, onProgress = null) {
   257→  let attempts = 0;
   258→
   259→  while (attempts < MAX_POLL_ATTEMPTS) {
   260→    attempts++;
   261→
   262→    const status = await checkTaskStatus(taskId);
   263→
   264→    if (onProgress) {
   265→      onProgress(status.progress, status.status);
   266→    }
   267→
   268→    if (status.status === 'completed') {
   269→      if (!status.audioUrl) {
   270→        throw new Error('Mureka completed but no audio URL provided');
   271→      }
   272→      return status;
   273→    }
   274→
   275→    if (status.status === 'failed') {
   276→      throw new Error(status.error || 'Mureka generation failed');
   277→    }
   278→
   279→    if (status.status === 'cancelled') {
   280→      throw new Error('Mureka generation was cancelled');
   281→    }
   282→
   283→    // Wait before polling again
   284→    await new Promise(resolve => setTimeout(resolve, POLL_INTERVAL));
   285→  }
   286→
   287→  throw new Error('Mureka generation timed out after 10 minutes');
   288→}
   289→
   290→/**
   291→ * Download audio file from URL
   292→ * @param {string} url - Audio URL
   293→ * @returns {Promise<Buffer>} Audio buffer
   294→ */
   295→async function downloadAudio(url) {
   296→  return new Promise((resolve, reject) => {
   297→    const protocol = url.startsWith('https') ? https : require('http');
   298→
   299→    protocol.get(url, (res) => {
   300→      if (res.statusCode === 302 || res.statusCode === 301) {
   301→        // Handle redirect
   302→        return downloadAudio(res.headers.location).then(resolve).catch(reject);
   303→      }
   304→
   305→      if (res.statusCode !== 200) {
   306→        reject(new Error(`Failed to download audio: HTTP ${res.statusCode}`));
   307→        return;
   308→      }
   309→
   310→      const chunks = [];
   311→      res.on('data', (chunk) => chunks.push(chunk));
   312→      res.on('end', () => resolve(Buffer.concat(chunks)));
   313→      res.on('error', reject);
   314→    }).on('error', reject);
   315→  });
   316→}
   317→
   318→/**
   319→ * Convert audio buffer to preview MP3
   320→ * @param {Buffer} wavBuffer - WAV audio buffer
   321→ * @returns {Promise<Buffer>} MP3 buffer
   322→ */
   323→async function createPreviewMp3(wavBuffer) {
   324→  const tempDir = process.env.TEMP_DIR || '/tmp/splice-music';
   325→  const tempWav = path.join(tempDir, `preview_${Date.now()}.wav`);
   326→  const tempMp3 = path.join(tempDir, `preview_${Date.now()}.mp3`);
   327→
   328→  try {
   329→    // Ensure temp dir exists
   330→    await fs.mkdir(tempDir, { recursive: true });
   331→
   332→    // Write WAV to temp file
   333→    await fs.writeFile(tempWav, wavBuffer);
   334→
   335→    // Convert to MP3 using FFmpeg
   336→    await execAsync(`ffmpeg -y -i "${tempWav}" -codec:a libmp3lame -qscale:a 4 "${tempMp3}"`, {
   337→      timeout: 30000
   338→    });
   339→
   340→    // Read MP3 buffer
   341→    const mp3Buffer = await fs.readFile(tempMp3);
   342→
   343→    return mp3Buffer;
   344→  } finally {
   345→    // Cleanup temp files
   346→    try {
   347→      await fs.unlink(tempWav);
   348→      await fs.unlink(tempMp3);
   349→    } catch (e) {
   350→      // Ignore cleanup errors
   351→    }
   352→  }
   353→}
   354→
   355→/**
   356→ * Generate music title from options
   357→ * @param {Object} options - Generation options
   358→ * @returns {string} Generated title
   359→ */
   360→function generateTitle(options) {
   361→  const parts = [];
   362→
   363→  // Add mood
   364→  if (options.mood) {
   365→    parts.push(options.mood.charAt(0).toUpperCase() + options.mood.slice(1));
   366→  }
   367→
   368→  // Add reference song if available
   369→  if (options.referenceSong?.title) {
   370→    parts.push(`(${options.referenceSong.title} style)`);
   371→  }
   372→
   373→  // Add genre if available
   374→  if (options.referenceSong?.genres?.[0]) {
   375→    parts.push(options.referenceSong.genres[0]);
   376→  }
   377→
   378→  // Fallback
   379→  if (parts.length === 0) {
   380→    parts.push('AI Generated');
   381→  }
   382→
   383→  parts.push('Music');
   384→
   385→  return parts.join(' ');
   386→}
   387→
   388→/**
   389→ * Full music generation flow
   390→ * @param {Object} options - Generation options
   391→ * @param {Function} onProgress - Progress callback
   392→ * @returns {Promise<Object>} Generated music result
   393→ */
   394→async function generateMusic(options, onProgress = null) {
   395→  // Validate credentials
   396→  if (!hasMurekaCredentials()) {
   397→    throw new Error('Mureka API key not configured. Set MUREKA_API_KEY environment variable.');
   398→  }
   399→
   400→  // Submit generation request
   401→  const { taskId, status, estimatedTime } = await submitGeneration(options);
   402→
   403→  if (onProgress) {
   404→    onProgress(10, 'submitted');
   405→  }
   406→
   407→  // Poll for completion
   408→  const result = await pollForCompletion(taskId, (progress, status) => {
   409→    if (onProgress) {
   410→      // Scale progress to 10-90 range (10 for submitted, 90 for downloading)
   411→      const scaledProgress = 10 + Math.floor(progress * 0.8);
   412→      onProgress(scaledProgress, status);
   413→    }
   414→  });
   415→
   416→  if (onProgress) {
   417→    onProgress(90, 'downloading');
   418→  }
   419→
   420→  // Download audio
   421→  const audioBuffer = await downloadAudio(result.audioUrl);
   422→
   423→  if (onProgress) {
   424→    onProgress(95, 'processing');
   425→  }
   426→
   427→  // Create preview MP3
   428→  let previewBuffer;
   429→  try {
   430→    previewBuffer = await createPreviewMp3(audioBuffer);
   431→  } catch (error) {
   432→    console.warn('Failed to create preview MP3:', error.message);
   433→    previewBuffer = null;
   434→  }
   435→
   436→  // Generate title
   437→  const title = generateTitle(options);
   438→
   439→  if (onProgress) {
   440→    onProgress(100, 'completed');
   441→  }
   442→
   443→  return {
   444→    title,
   445→    taskId: result.taskId,
   446→    duration: result.duration || options.duration || DEFAULT_DURATION,
   447→    audioBuffer,
   448→    previewBuffer,
   449→    audioUrl: result.audioUrl,
   450→    previewUrl: result.previewUrl
   451→  };
   452→}
   453→
   454→/**
   455→ * Get available moods
   456→ * @returns {Object} Mood presets
   457→ */
   458→function getAvailableMoods() {
   459→  return Object.keys(MOOD_PRESETS).map(key => ({
   460→    id: key,
   461→    name: key.charAt(0).toUpperCase() + key.slice(1),
   462→    description: MOOD_PRESETS[key]
   463→  }));
   464→}
   465→
   466→/**
   467→ * Get available instrument presets
   468→ * @returns {Object} Instrument presets
   469→ */
   470→function getAvailableInstruments() {
   471→  return Object.keys(INSTRUMENT_PRESETS).map(key => ({
   472→    id: key,
   473→    name: key.charAt(0).toUpperCase() + key.slice(1),
   474→    instruments: INSTRUMENT_PRESETS[key]
   475→  }));
   476→}
   477→
   478→/**
   479→ * Validate generation options
   480→ * @param {Object} options - Generation options
   481→ * @returns {{valid: boolean, errors: string[]}}
   482→ */
   483→function validateOptions(options) {
   484→  const errors = [];
   485→
   486→  // Check duration
   487→  if (options.duration !== undefined) {
   488→    if (typeof options.duration !== 'number') {
   489→      errors.push('Duration must be a number');
   490→    } else if (options.duration < MIN_DURATION) {
   491→      errors.push(`Duration must be at least ${MIN_DURATION} seconds`);
   492→    } else if (options.duration > MAX_DURATION) {
   493→      errors.push(`Duration must be at most ${MAX_DURATION} seconds`);
   494→    }
   495→  }
   496→
   497→  // Check mood
   498→  if (options.mood && !MOOD_PRESETS[options.mood]) {
   499→    // Allow custom moods, just log a warning
   500→    console.warn(`Unknown mood preset: ${options.mood}. Using as custom description.`);
   501→  }
   502→
   503→  // Check instruments
   504→  if (options.instruments && !Array.isArray(options.instruments)) {
   505→    errors.push('Instruments must be an array');
   506→  }
   507→
   508→  return {
   509→    valid: errors.length === 0,
   510→    errors
   511→  };
   512→}
   513→
   514→module.exports = {
   515→  hasMurekaCredentials,
   516→  buildMurekaPrompt,
   517→  clampDuration,
   518→  submitGeneration,
   519→  checkTaskStatus,
   520→  pollForCompletion,
   521→  downloadAudio,
   522→  createPreviewMp3,
   523→  generateTitle,
   524→  generateMusic,
   525→  getAvailableMoods,
   526→  getAvailableInstruments,
   527→  validateOptions,
   528→  MOOD_PRESETS,
   529→  INSTRUMENT_PRESETS,
   530→  MIN_DURATION,
   531→  MAX_DURATION,
   532→  DEFAULT_DURATION
   533→};
