   447→    const result = await generateMusicRequest(options);
   448→
   449→    setMusicStatus(`Generation started! Job ID: ${result.jobId}`, 'success');
   450→
   451→    // Add to jobs list and start polling
   452→    addJobToList(result.jobId, options);
   453→    startPollingJob(result.jobId);
   454→
   455→    // Clear form
   456→    handleClearReference();
   457→    if (promptInput) promptInput.value = '';
   458→
   459→  } catch (error) {
   460→    console.error('Generation error:', error);
   461→    setMusicStatus(`Error: ${error.message}`, 'error');
   462→  } finally {
   463→    musicState.isGenerating = false;
   464→    generateBtn.disabled = false;
   465→    generateBtn.textContent = 'Generate Music';
   466→  }
   467→}
   468→
   469→/**
   470→ * Add job to the jobs list
   471→ * @param {string} jobId - Job ID
   472→ * @param {Object} options - Job options
   473→ */
   474→function addJobToList(jobId, options) {
   475→  const job = {
   476→    jobId,
   477→    status: 'pending',
   478→    progress: 0,
   479→    mood: options.mood,
   480→    duration: options.duration,
   481→    createdAt: new Date().toISOString()
   482→  };
   483→
   484→  musicState.jobs.unshift(job);
   485→  renderJobsList();
   486→}
   487→
   488→/**
   489→ * Start polling for job completion
   490→ * @param {string} jobId - Job ID
   491→ */
   492→function startPollingJob(jobId) {
   493→  // Clear any existing poll
   494→  if (musicState.pollInterval) {
   495→    clearInterval(musicState.pollInterval);
   496→  }
   497→
   498→  musicState.pollInterval = setInterval(async () => {
   499→    try {
   500→      const status = await getJobStatus(jobId);
   501→
   502→      // Update job in list
   503→      const jobIndex = musicState.jobs.findIndex(j => j.jobId === jobId);
   504→      if (jobIndex !== -1) {
   505→        musicState.jobs[jobIndex] = {
   506→          ...musicState.jobs[jobIndex],
   507→          ...status
   508→        };
   509→        renderJobsList();
   510→      }
   511→
   512→      // Check if complete or failed
   513→      if (status.status === 'completed' || status.status === 'failed') {
   514→        clearInterval(musicState.pollInterval);
   515→        musicState.pollInterval = null;
   516→
   517→        if (status.status === 'completed') {
   518→          setMusicStatus('Music generation completed!', 'success');
   519→          loadMusicLibrary(); // Refresh library
   520→        } else {
   521→          setMusicStatus(`Generation failed: ${status.failedReason || 'Unknown error'}`, 'error');
   522→        }
   523→      }
   524→
   525→    } catch (error) {
   526→      console.error('Polling error:', error);
   527→    }
   528→  }, MUSIC_POLL_INTERVAL);
   529→}
   530→
   531→/**
   532→ * Render jobs list UI
   533→ */
   534→function renderJobsList() {
   535→  const container = document.getElementById('musicJobsList');
   536→  if (!container) return;
   537→
   538→  if (musicState.jobs.length === 0) {
   539→    container.innerHTML = '<div class="music-empty">No generation jobs yet</div>';
   540→    return;
   541→  }
   542→
   543→  container.innerHTML = musicState.jobs.map(job => `
   544→    <div class="music-job-item ${job.status}" data-job-id="${job.jobId}">
   545→      <div class="music-job-status ${job.status}"></div>
   546→      <div class="music-job-info">
   547→        <span class="music-job-mood">${job.mood || 'Music'}</span>
   548→        <span class="music-job-duration">${job.duration}s</span>
   549→      </div>
   550→      <div class="music-job-progress">
   551→        ${job.status === 'completed' ? '✓' : job.status === 'failed' ? '✗' : `${job.progress}%`}
   552→      </div>
   553→    </div>
   554→  `).join('');
   555→}
   556→
   557→/**
   558→ * Load and display music library
   559→ */
   560→async function loadMusicLibrary() {
   561→  const container = document.getElementById('musicLibrary');
   562→  if (!container) return;
   563→
   564→  try {
   565→    container.innerHTML = '<div class="music-loading">Loading library...</div>';
   566→
   567→    const library = await getMusicLibrary();
   568→
   569→    if (library.length === 0) {
   570→      container.innerHTML = '<div class="music-empty">Your music library is empty</div>';
   571→      return;
   572→    }
   573→
   574→    container.innerHTML = library.map(item => `
   575→      <div class="music-library-item" data-job-id="${item.jobId}">
   576→        <div class="music-item-info">
   577→          <div class="music-item-title">${escapeHtml(item.title || 'Untitled')}</div>
   578→          <div class="music-item-meta">${item.duration}s • ${item.mood || 'Music'}</div>
   579→        </div>
   580→        <div class="music-item-actions">
   581→          <button class="music-action-btn" data-action="preview" data-job-id="${item.jobId}" title="Preview">▶</button>
   582→          <button class="music-action-btn" data-action="import" data-job-id="${item.jobId}" title="Import to Timeline">+</button>
   583→          <button class="music-action-btn danger" data-action="delete" data-job-id="${item.jobId}" title="Delete">×</button>
   584→        </div>
   585→      </div>
   586→    `).join('');
   587→
   588→    // Add event listeners
   589→    container.querySelectorAll('.music-action-btn').forEach(btn => {
   590→      btn.addEventListener('click', handleLibraryAction);
   591→    });
   592→
   593→  } catch (error) {
   594→    console.error('Library load error:', error);
   595→    container.innerHTML = '<div class="music-error">Failed to load library</div>';
   596→  }
   597→}
   598→
   599→/**
   600→ * Handle library action (preview, import, delete)
   601→ * @param {Event} event - Click event
   602→ */
   603→async function handleLibraryAction(event) {
   604→  const btn = event.target;
   605→  const action = btn.dataset.action;
   606→  const jobId = btn.dataset.jobId;
   607→
   608→  switch (action) {
   609→    case 'preview':
   610→      await previewMusic(jobId);
   611→      break;
   612→    case 'import':
   613→      await importMusicToTimeline(jobId);
   614→      break;
   615→    case 'delete':
   616→      await confirmDeleteMusic(jobId);
   617→      break;
   618→  }
   619→}
   620→
   621→/**
   622→ * Preview music file
   623→ * @param {string} jobId - Job ID
   624→ */
   625→async function previewMusic(jobId) {
   626→  try {
   627→    setMusicStatus('Loading preview...', 'info');
   628→
   629→    const music = await getMusicFile(jobId);
   630→
   631→    if (!music.previewUrl && !music.downloadUrl) {
   632→      setMusicStatus('No preview available', 'warning');
   633→      return;
   634→    }
   635→
   636→    // Create or update audio player
   637→    if (!musicState.audioPlayer) {
   638→      musicState.audioPlayer = new Audio();
   639→      musicState.audioPlayer.addEventListener('ended', () => {
   640→        setMusicStatus('Preview finished', 'info');
   641→      });
   642→    }
   643→
   644→    musicState.audioPlayer.src = music.previewUrl || music.downloadUrl;
   645→    musicState.audioPlayer.play();
   646→
   647→    setMusicStatus('Playing preview...', 'success');
   648→
   649→  } catch (error) {
   650→    console.error('Preview error:', error);
   651→    setMusicStatus(`Preview error: ${error.message}`, 'error');
   652→  }
   653→}
   654→
   655→/**
   656→ * Import music to Premiere Pro timeline
   657→ * @param {string} jobId - Job ID
   658→ */
   659→async function importMusicToTimeline(jobId) {
   660→  try {
   661→    setMusicStatus('Downloading music...', 'info');
   662→
   663→    const music = await getMusicFile(jobId);
   664→
   665→    if (!music.downloadUrl) {
   666→      setMusicStatus('Download URL not available', 'error');
   667→      return;
   668→    }
   669→
   670→    setMusicStatus('Importing to timeline...', 'info');
   671→
   672→    // Download file
   673→    const response = await fetch(music.downloadUrl);
   674→    const blob = await response.blob();
   675→
   676→    // Get temp folder access
   677→    const tempFolder = await getTempFolder();
   678→    if (!tempFolder) {
   679→      setMusicStatus('Could not access temp folder', 'error');
   680→      return;
   681→    }
   682→
   683→    // Save to temp file
   684→    const fileName = `splice_music_${jobId}.wav`;
   685→    const tempFile = await tempFolder.createFile(fileName, { overwrite: true });
   686→    const arrayBuffer = await blob.arrayBuffer();
   687→    await tempFile.write(arrayBuffer);
   688→
   689→    // Import to project
   690→    const pproBuilder = require('premierepro').pproBuilder;
   691→    const project = await pproBuilder.Project.getActiveProject();
   692→
   693→    if (!project) {
   694→      setMusicStatus('No active project', 'error');
   695→      return;
   696→    }
   697→
   698→    // Import file
   699→    const imported = await project.importFiles([tempFile.nativePath]);
   700→
   701→    if (imported && imported.length > 0) {
   702→      setMusicStatus('Music imported to project!', 'success');
   703→
   704→      // Optionally insert at playhead
   705→      const sequence = await pproBuilder.Sequence.getActiveSequence();
   706→      if (sequence) {
   707→        // TODO: Insert clip at playhead position
   708→        setMusicStatus('Music imported and ready to use!', 'success');
   709→      }
   710→    } else {
   711→      setMusicStatus('Import failed', 'error');
   712→    }
   713→
   714→  } catch (error) {
   715→    console.error('Import error:', error);
   716→    setMusicStatus(`Import error: ${error.message}`, 'error');
   717→  }
   718→}
   719→
   720→/**
   721→ * Confirm and delete music
   722→ * @param {string} jobId - Job ID
   723→ */
   724→async function confirmDeleteMusic(jobId) {
   725→  const confirmed = await showConfirmModal(
   726→    'Delete Music',
   727→    'Are you sure you want to delete this music file?'
   728→  );
   729→
   730→  if (!confirmed) return;
   731→
   732→  try {
   733→    setMusicStatus('Deleting...', 'info');
   734→    await deleteMusicFile(jobId);
   735→    setMusicStatus('Music deleted', 'success');
   736→    loadMusicLibrary();
   737→  } catch (error) {
   738→    console.error('Delete error:', error);
   739→    setMusicStatus(`Delete error: ${error.message}`, 'error');
   740→  }
   741→}
   742→
   743→/**
   744→ * Update music credits display
   745→ */
   746→async function updateMusicCreditsDisplay() {
   747→  const badge = document.getElementById('musicCreditsBadge');
   748→  if (!badge) return;
   749→
   750→  try {
   751→    const credits = await getMusicCredits();
   752→    badge.textContent = `${credits.remaining}/${credits.total} songs`;
   753→    badge.className = 'music-credits-badge';
   754→
   755→    if (credits.remaining === 0) {
   756→      badge.classList.add('empty');
   757→    } else if (credits.remaining <= 2) {
   758→      badge.classList.add('low');
   759→    }
   760→  } catch (error) {
   761→    badge.textContent = 'Credits: --';
   762→  }
   763→}
   764→
   765→/**
   766→ * Set music status message
   767→ * @param {string} message - Status message
   768→ * @param {string} type - Message type (info, success, warning, error)
   769→ */
   770→function setMusicStatus(message, type = 'info') {
   771→  const status = document.getElementById('musicStatus');
   772→  if (!status) return;
   773→
   774→  status.textContent = message;
   775→  status.className = `music-status ${type}`;
   776→  status.style.display = 'block';
   777→
   778→  // Auto-hide after delay for non-error messages
   779→  if (type !== 'error') {
   780→    setTimeout(() => {
   781→      status.style.display = 'none';
   782→    }, 5000);
   783→  }
   784→}
   785→
   786→/**
   787→ * Get temp folder for file operations
   788→ * @returns {Promise<Folder|null>}
   789→ */
   790→async function getTempFolder() {
   791→  try {
   792→    const uxpFs = require('uxp').storage.localFileSystem;
   793→    return await uxpFs.getTemporaryFolder();
   794→  } catch (error) {
   795→    console.error('Temp folder error:', error);
   796→    return null;
   797→  }
   798→}
   799→
   800→/**
   801→ * Escape HTML special characters
   802→ * @param {string} str - String to escape
   803→ * @returns {string} Escaped string
   804→ */
   805→function escapeHtml(str) {
   806→  if (!str) return '';
   807→  const div = document.createElement('div');
   808→  div.textContent = str;
   809→  return div.innerHTML;
   810→}
   811→
   812→/**
   813→ * Simple debounce function
   814→ * @param {Function} fn - Function to debounce
   815→ * @param {number} delay - Delay in ms
   816→ * @returns {Function} Debounced function
   817→ */
   818→function debounce(fn, delay) {
   819→  let timeoutId;
   820→  return function (...args) {
   821→    clearTimeout(timeoutId);
   822→    timeoutId = setTimeout(() => fn.apply(this, args), delay);
   823→  };
   824→}
   825→
   826→// Export for use in other modules
   827→window.musicModule = {
   828→  init: initMusicModule,
   829→  identifySong,
   830→  generateMusicRequest,
   831→  getMusicLibrary,
   832→  getMusicCredits,
   833→  loadMusicLibrary,
   834→  previewMusic,
   835→  importMusicToTimeline,
   836→  MOOD_OPTIONS,
   837→  INSTRUMENT_OPTIONS,
   838→  DURATION_OPTIONS
   839→};
