     1→/**
     2→ * SPLICE CEP Credits Display Module
     3→ *
     4→ * Fetches and displays user's remaining hours from the billing backend.
     5→ * Port of UXP credits.js for CEP compatibility.
     6→ */
     7→
     8→// Track fetch error state for retry UI
     9→let lastCreditsError = null;
    10→let creditsRetryCount = 0;
    11→const MAX_CREDITS_RETRIES = 3;
    12→
    13→// Store current credits globally for access checks
    14→let currentCredits = null;
    15→
    16→// Store interval reference for cleanup
    17→let creditsRefreshInterval = null;
    18→
    19→/**
    20→ * Fetch user's credit balance from backend
    21→ * @returns {Promise<Object | null>} Credit data including isolation hours, or error object
    22→ */
    23→async function fetchCredits() {
    24→    const settings = getSettings();
    25→    const customerId = settings.customerId;
    26→
    27→    if (!customerId) {
    28→        console.log('[SPLICE] No customerId configured - credits display disabled');
    29→        lastCreditsError = null;
    30→        return null;
    31→    }
    32→
    33→    try {
    34→        const response = await fetchWithTimeout(`${getBackendUrl()}/credits`, {
    35→            method: 'GET',
    36→            headers: {
    37→                'x-stripe-customer-id': customerId
    38→            }
    39→        }, SPLICE_CONFIG.FETCH_TIMEOUT);
    40→
    41→        if (!response.ok) {
    42→            const errorMsg = await parseErrorResponse(response);
    43→            console.error('[SPLICE] Credits fetch failed:', errorMsg);
    44→            lastCreditsError = errorMsg;
    45→            return { _error: true, message: errorMsg };
    46→        }
    47→
    48→        const data = await response.json();
    49→        lastCreditsError = null;
    50→        creditsRetryCount = 0;
    51→        return {
    52→            hoursRemaining: data.hoursRemaining || 0,
    53→            hoursTotal: data.hoursTotal || 0,
    54→            tierName: data.tierName || 'Free',
    55→            tier: data.tier || 'starter',
    56→            // Isolation hours
    57→            isolationHoursRemaining: data.isolationHoursRemaining || 0,
    58→            isolationHoursTotal: data.isolationHoursTotal || 0,
    59→            hasIsolationAccess: data.hasIsolationAccess || false,
    60→            isolationOverageRate: data.isolationOverageRate || 0.08,
    61→            // Trial info
    62→            trialDaysRemaining: data.trialDaysRemaining || null,
    63→            isOnTrial: data.isOnTrial || false
    64→        };
    65→    } catch (err) {
    66→        console.error('[SPLICE] Credits fetch error:', err);
    67→        lastCreditsError = err.message || 'Connection failed';
    68→        return { _error: true, message: lastCreditsError };
    69→    }
    70→}
    71→
    72→/**
    73→ * Clear the credits cache (call on logout)
    74→ */
    75→function clearCreditsCache() {
    76→    currentCredits = null;
    77→    lastCreditsError = null;
    78→    creditsRetryCount = 0;
    79→    console.log('[SPLICE] Credits cache cleared');
    80→}
    81→
    82→/**
    83→ * Update the credit display in the UI
    84→ * @param {Object|null} credits - Credit data, error object, or null if unavailable
    85→ */
    86→function updateCreditDisplay(credits) {
    87→    const creditBadge = document.getElementById('creditBadge');
    88→    if (!creditBadge) return;
    89→
    90→    // Handle no customer ID configured
    91→    if (!credits) {
    92→        currentCredits = null;
    93→        creditBadge.style.display = 'flex';
    94→        creditBadge.textContent = 'Login';
    95→        creditBadge.title = 'Click to enter license key';
    96→        creditBadge.classList.remove('ok', 'low');
    97→        creditBadge.classList.add('login');
    98→        return;
    99→    }
   100→
   101→    // Handle error state - show error badge with retry
   102→    if (credits._error) {
   103→        currentCredits = null;
   104→        creditBadge.style.display = 'flex';
   105→        creditBadge.textContent = 'Retry';
   106→        creditBadge.title = `Error: ${credits.message}\nClick to retry`;
   107→        creditBadge.classList.remove('ok', 'low', 'login');
   108→        creditBadge.classList.add('error');
   109→        return;
   110→    }
   111→
   112→    // Normal credit display
   113→    currentCredits = credits;
   114→    creditBadge.style.display = 'flex';
   115→
   116→    // Show trial status if on trial
   117→    if (credits.isOnTrial && credits.trialDaysRemaining) {
   118→        creditBadge.textContent = `Trial: ${credits.trialDaysRemaining}d`;
   119→        creditBadge.classList.remove('error', 'login', 'ok', 'low');
   120→        creditBadge.classList.add('trial');
   121→    } else {
   122→        creditBadge.textContent = `${credits.hoursRemaining.toFixed(1)} hrs`;
   123→        creditBadge.classList.remove('error', 'login', 'trial');
   124→    }
   125→
   126→    // Build tooltip with isolation info if available
   127→    let tooltip = `${credits.tierName}: ${credits.hoursRemaining.toFixed(1)} / ${credits.hoursTotal} hours`;
   128→    if (credits.isOnTrial && credits.trialDaysRemaining) {
   129→        tooltip = `Trial: ${credits.trialDaysRemaining} days remaining\n` + tooltip;
   130→    }
   131→    if (credits.hasIsolationAccess) {
   132→        tooltip += `\nIsolation: ${(credits.isolationHoursRemaining * 60).toFixed(0)} / ${(credits.isolationHoursTotal * 60).toFixed(0)} min`;
   133→    }
   134→    creditBadge.title = tooltip;
   135→
   136→    // Color based on remaining hours
   137→    if (credits.hoursRemaining <= 1) {
   138→        creditBadge.classList.add('low');
   139→        creditBadge.classList.remove('ok');
   140→    } else {
   141→        creditBadge.classList.add('ok');
   142→        creditBadge.classList.remove('low');
   143→    }
   144→
   145→    // Update isolation checkbox state based on tier access
   146→    updateIsolationCheckboxState(credits);
   147→}
   148→
   149→/**
   150→ * Update the isolation checkbox based on tier access
   151→ */
   152→function updateIsolationCheckboxState(credits) {
   153→    const isolatedCheckbox = document.getElementById('sourceIsolated');
   154→    const tierBadge = isolatedCheckbox?.parentElement?.querySelector('.tier-badge');
   155→
   156→    if (!isolatedCheckbox) return;
   157→
   158→    if (!credits || !credits.hasIsolationAccess) {
   159→        // Disable isolation for non-Pro/Team users
   160→        isolatedCheckbox.disabled = true;
   161→        isolatedCheckbox.checked = false;
   162→        if (tierBadge) {
   163→            tierBadge.textContent = 'Pro+';
   164→            tierBadge.style.opacity = '1';
   165→        }
   166→    } else {
   167→        // Enable isolation for Pro/Team users
   168→        isolatedCheckbox.disabled = false;
   169→        if (tierBadge) {
   170→            // Show remaining isolation time
   171→            const minsRemaining = (credits.isolationHoursRemaining * 60).toFixed(0);
   172→            tierBadge.textContent = `${minsRemaining} min`;
   173→            tierBadge.style.opacity = credits.isolationHoursRemaining > 0 ? '1' : '0.6';
   174→        }
   175→    }
   176→}
   177→
   178→/**
   179→ * Check if user can use isolation and get cost estimate
   180→ * @param {number} estimatedMinutes - Estimated audio duration in minutes
   181→ * @returns {Object} Access info with allowed, message, overageCost
   182→ */
   183→function checkIsolationAccess(estimatedMinutes = 0) {
   184→    if (!currentCredits) {
   185→        return { allowed: false, message: 'Credits not loaded', overageCost: 0 };
   186→    }
   187→
   188→    if (!currentCredits.hasIsolationAccess) {
   189→        return {
   190→            allowed: false,
   191→            message: 'Vocal isolation requires Pro or Team tier',
   192→            upgradeRequired: true,
   193→            overageCost: 0
   194→        };
   195→    }
   196→
   197→    const estimatedHours = estimatedMinutes / 60;
   198→    const remaining = currentCredits.isolationHoursRemaining;
   199→
   200→    if (remaining >= estimatedHours) {
   201→        return {
   202→            allowed: true,
   203→            message: `Using ${estimatedMinutes.toFixed(1)} min of included isolation time`,
   204→            overageCost: 0
   205→        };
   206→    }
   207→
   208→    // Calculate overage
   209→    const overageMinutes = estimatedMinutes - (remaining * 60);
   210→    const overageCost = overageMinutes * currentCredits.isolationOverageRate;
   211→
   212→    return {
   213→        allowed: true,
   214→        message: `${remaining > 0 ? `${(remaining * 60).toFixed(0)} included + ` : ''}${overageMinutes.toFixed(0)} min overage ($${overageCost.toFixed(2)})`,
   215→        overageCost,
   216→        overageMinutes
   217→    };
   218→}
   219→
   220→/**
   221→ * Get current credits (for access checks)
   222→ */
   223→function getCurrentCredits() {
   224→    return currentCredits;
   225→}
   226→
   227→/**
   228→ * Initialize credits display
   229→ * Fetches credits on load and sets up periodic refresh
   230→ */
   231→async function initCredits() {
   232→    // Clear any existing interval to prevent duplicates
   233→    if (creditsRefreshInterval) {
   234→        clearInterval(creditsRefreshInterval);
   235→    }
   236→
   237→    // Initial fetch
   238→    const credits = await fetchCredits();
   239→    updateCreditDisplay(credits);
   240→
   241→    // Refresh credits every 5 minutes
   242→    creditsRefreshInterval = setInterval(async () => {
   243→        const refreshedCredits = await fetchCredits();
   244→        updateCreditDisplay(refreshedCredits);
   245→    }, 5 * 60 * 1000);
   246→}
   247→
   248→/**
   249→ * Cleanup credits module - call on plugin unload
   250→ */
   251→function cleanupCredits() {
   252→    if (creditsRefreshInterval) {
   253→        clearInterval(creditsRefreshInterval);
   254→        creditsRefreshInterval = null;
   255→    }
   256→}
   257→
   258→/**
   259→ * Manually refresh credits (e.g., after a processing operation)
   260→ */
   261→async function refreshCredits() {
   262→    const credits = await fetchCredits();
   263→    updateCreditDisplay(credits);
   264→    return credits;
   265→}
   266→
   267→// Export for window access
   268→window.fetchCredits = fetchCredits;
   269→window.clearCreditsCache = clearCreditsCache;
   270→window.updateCreditDisplay = updateCreditDisplay;
   271→window.checkIsolationAccess = checkIsolationAccess;
   272→window.getCurrentCredits = getCurrentCredits;
   273→window.initCredits = initCredits;
   274→window.cleanupCredits = cleanupCredits;
   275→window.refreshCredits = refreshCredits;
