/**
 * SPLICE CEP Panel Styles
 * Premium "Deep Space" Theme
 */

:root {
    /* Palette */
    --bg-core: #121212;
    --bg-panel: var(--bg-panel);
    --bg-card: rgba(30, 30, 30, 0.6);
    --glass-border: rgba(255, 255, 255, 0.08);
    --glass-highlight: rgba(255, 255, 255, 0.03);

    /* Accents */
    --accent-primary: var(--accent-primary);
    --accent-gradient: linear-gradient(135deg, var(--accent-primary), #a64dff);
    --accent-glow: 0 0 20px rgba(74, 158, 255, 0.15);

    /* Functional Colors */
    --text-main: #f0f0f0;
    --text-muted: var(--text-muted)888;
    --success: #00e676;
    --error: #ff5252;

    /* Spacing & Radius */
    --radius-sm: 6px;
    --radius-md: 12px;
    --radius-lg: 16px;
    --spacing-xs: 4px;
    --spacing-sm: 8px;
    --spacing-md: 16px;

    /* Transitions */
    --ease-smooth: cubic-bezier(0.4, 0.0, 0.2, 1);
}

/* Reset & Base */
* {
    box-sizing: border-box;
    margin: 0;
    padding: 0;
}

body {
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif;
    background: var(--bg-core);
    color: var(--text-main);
    margin: 0;
    padding: var(--spacing-md);
    height: 100vh;
    overflow-y: auto;
    overflow-x: hidden;
    font-size: 12px;
    line-height: 1.5;
    -webkit-font-smoothing: antialiased;
}

/* Loading Overlay */
#loading-overlay {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: var(--bg-core);
    z-index: 9999;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    transition: opacity 0.5s var(--ease-smooth), visibility 0.5s;
}

#loading-overlay.hidden {
    opacity: 0;
    visibility: hidden;
    pointer-events: none;
}

.loader-logo {
    width: 48px;
    height: 48px;
    border-radius: 50%;
    background: var(--accent-gradient);
    animation: pulse-ring 2s infinite;
    margin-bottom: var(--spacing-md);
    box-shadow: var(--accent-glow);
}

.loader-text {
    font-size: 11px;
    color: var(--text-muted);
    letter-spacing: 1px;
    text-transform: uppercase;
    font-weight: 500;
}

@keyframes pulse-ring {
    0% {
        transform: scale(0.95);

