    min-width: 70px;
    color: var(--accent-primary);
    font-weight: 500;
}

.decision-speaker {
    min-width: 60px;
    color: #e1e1e1;
}

.decision-speaker.wide-shot {
    color: var(--text-muted);
}

.decision-reason {
    flex: 1;
    color: #666;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}

.decision-seek {
    background: transparent;
    border: 1px solid var(--glass-border);
    color: var(--text-muted);
    width: 20px;
    height: 20px;
    border-radius: 3px;
    cursor: pointer;
    font-size: 10px;
}

.decision-seek:hover {
    background: #333;
    color: #e1e1e1;
}

/* Multitrack Preview Actions */
.multitrack-preview-actions {
    display: flex;
    justify-content: flex-end;
}

/* Animated Captions Section */
.captions-section {
    background: var(--bg-card);
    border-radius: 6px;
    border: 1px solid var(--glass-border);
    margin-bottom: 10px;
}

.captions-panel {
    padding: 0 12px 12px;
    border-top: 1px solid var(--glass-border);
}

.captions-panel.collapsed {
    display: none;
}

/* Caption Templates */
.caption-templates {
    margin-bottom: 12px;
}

.caption-templates>label {
    display: block;
    font-size: 10px;
    color: var(--text-muted);
    margin-bottom: 6px;
}

.caption-template-gallery {
    display: flex;
    gap: 8px;
    overflow-x: auto;
    padding: 4px 0;
}

.caption-template-card {
    min-width: 80px;
    padding: 8px;
    background: var(--bg-panel);
    border-radius: 4px;
    border: 2px solid transparent;
    cursor: pointer;
    transition: border-color 0.2s;
}

.caption-template-card:hover {
    border-color: #555;
}

.caption-template-card.selected {
    border-color: var(--accent-primary);
}

