    border-radius: 4px;
    border: 1px solid var(--glass-border);
}

/* Export Actions */
.reframe-export-actions {
    display: flex;
    gap: 8px;
    margin-bottom: 10px;
}

.reframe-export-actions .btn {
    flex: 1;
}

/* Export Results */
.reframe-export-results {
    margin-bottom: 10px;
}

.export-summary {
    padding: 8px;
    background: var(--bg-panel);
    border-radius: 4px;
    margin-bottom: 8px;
    font-size: 10px;
    color: var(--text-muted);
}

.export-count {
    color: #28a745;
    font-weight: 500;
}

.export-list {
    display: flex;
    flex-direction: column;
    gap: 4px;
}

.export-item {
    display: flex;
    gap: 8px;
    align-items: center;
    padding: 6px 8px;
    background: var(--bg-panel);
    border-radius: 4px;
    font-size: 10px;
}

.export-format {
    min-width: 80px;
    color: #e1e1e1;
    font-weight: 500;
}

.export-dims {
    flex: 1;
    color: var(--text-muted);
}

.copy-ffmpeg-btn {
    font-size: 9px;
}

/* Music Section */
.music-section {
    background: var(--bg-card);
    border-radius: 6px;
    border: 1px solid var(--glass-border);
    margin-bottom: 10px;
}

.music-panel {
    padding: 0 12px 12px;
    border-top: 1px solid var(--glass-border);
}

.music-panel.collapsed {
    display: none;
}

/* Music Credits Badge */
.music-credits-badge {
    font-size: 9px;
    background: var(--bg-panel);
    border: 1px solid var(--glass-border);
    border-radius: 8px;
    padding: 2px 6px;
    margin-left: auto;
    color: var(--accent-primary);
}

.music-credits-badge.low {
    color: #dc3545;
}

/* Music Tabs */
.music-tabs {
    display: flex;
    gap: 4px;
    padding: 8px 0;
    border-bottom: 1px solid #333;
    margin-bottom: 10px;
}

.music-tab {
    flex: 1;
    padding: 6px 8px;
    background: var(--bg-panel);
    border: 1px solid var(--glass-border);
    border-radius: 4px;
    color: var(--text-muted);
    font-size: 10px;
    cursor: pointer;
    transition: all 0.2s;
}

.music-tab:hover {
    background: #333;
    color: #e1e1e1;
}

.music-tab.active {
    background: var(--accent-primary);
    border-color: var(--accent-primary);
    color: white;
}

/* Music Tab Content */
.music-tab-content {
    display: none;
}

.music-tab-content.active {
    display: block;
}

/* Mood Selection */
.music-mood-section,
.music-instrument-section {
    margin-bottom: 10px;
}

.music-mood-section>label,
.music-instrument-section>label {
    display: block;
    font-size: 10px;
    color: var(--text-muted);
    margin-bottom: 6px;
}

.music-mood-selector,
.music-instrument-selector {
    display: flex;
    flex-wrap: wrap;
    gap: 4px;
}

.music-mood-btn,
.music-instrument-btn,
.music-mood-card,
.music-instrument-card {
    display: inline-block;
    padding: 4px 8px;
    background: var(--bg-panel);
    border: 1px solid var(--glass-border);
    border-radius: 4px;
    color: #aaa;
    font-size: 9px;
    cursor: pointer;
    transition: all 0.2s;
}

.music-mood-btn:hover,
.music-instrument-btn:hover,
.music-mood-card:hover,
.music-instrument-card:hover {
    background: #333;
    color: #e1e1e1;
}

.music-mood-btn.selected,
.music-instrument-btn.selected,
.music-mood-card.selected,
.music-instrument-card.selected {
    background: var(--accent-primary);
    border-color: var(--accent-primary);
    color: white;
}

/* Mood Icons */
.music-mood-btn[data-mood="upbeat"]::before,
.music-mood-card[data-mood="upbeat"]::before {
    content: "🎉 ";
}

.music-mood-btn[data-mood="dramatic"]::before,
.music-mood-card[data-mood="dramatic"]::before {
    content: "🎭 ";
}

.music-mood-btn[data-mood="chill"]::before,
.music-mood-card[data-mood="chill"]::before {
    content: "😌 ";
}

.music-mood-btn[data-mood="energetic"]::before,
.music-mood-card[data-mood="energetic"]::before {
    content: "⚡ ";
}

.music-mood-btn[data-mood="mysterious"]::before,
.music-mood-card[data-mood="mysterious"]::before {
    content: "🔮 ";
}

.music-mood-btn[data-mood="romantic"]::before,
.music-mood-card[data-mood="romantic"]::before {
    content: "💕 ";
}

.music-mood-btn[data-mood="epic"]::before,
.music-mood-card[data-mood="epic"]::before {
    content: "🏔️ ";
}

.music-mood-btn[data-mood="playful"]::before,
.music-mood-card[data-mood="playful"]::before {
    content: "🎈 ";
}

.music-mood-btn[data-mood="melancholic"]::before,
.music-mood-card[data-mood="melancholic"]::before {
    content: "🌧️ ";
}

.music-mood-btn[data-mood="peaceful"]::before,
.music-mood-card[data-mood="peaceful"]::before {
    content: "🌿 ";
}

.music-mood-card[data-mood="relaxed"]::before {
    content: "😌 ";
}

.music-mood-card[data-mood="intense"]::before {
    content: "🔥 ";
}

.music-mood-card[data-mood="happy"]::before {
    content: "😊 ";
}

.music-mood-card[data-mood="neutral"]::before {
    content: "🎵 ";
}

/* Custom Prompt */
.music-custom-prompt {
    width: 100%;
    height: 50px;
    padding: 6px 8px;
    background: var(--bg-panel);
    border: 1px solid var(--glass-border);
    border-radius: 4px;
    color: #e1e1e1;
    font-size: 10px;
    resize: none;
}

.music-custom-prompt:focus {
    outline: none;
    border-color: var(--accent-primary);
}

/* Music Options */
.music-options {
    margin-bottom: 10px;
}

/* Generate Actions */
.music-generate-actions {
    display: flex;
    gap: 8px;
}

.music-generate-actions .btn {
    flex: 1;
}

/* Identify Result */
.music-identify-result {
    margin-top: 10px;
    padding: 10px;
    background: var(--bg-panel);
    border-radius: 4px;
    font-size: 10px;
    display: none;
}

.music-identify-result.visible {
    display: block;
}

.identify-song-title {
    font-weight: 600;
    color: #e1e1e1;
    margin-bottom: 4px;
}

.identify-song-artist {
    color: var(--accent-primary);
    margin-bottom: 4px;
}

.identify-song-album,
.identify-song-genres {
    color: var(--text-muted);
    font-size: 9px;
}

/* Music Library */
.music-library-list {
    max-height: 200px;
    overflow-y: auto;
}

.music-library-empty {
    text-align: center;
    color: #666;
    padding: 20px;
    font-size: 10px;
}

.music-library-item {
    display: flex;
    align-items: center;
    gap: 8px;
    padding: 8px;
    background: var(--bg-panel);
    border-radius: 4px;
    margin-bottom: 4px;
    cursor: pointer;
    transition: background 0.2s;
}

.music-library-item:hover {
    background: #333;
}

.music-library-item-info {
    flex: 1;
    min-width: 0;
}

.music-library-item-title {
    font-size: 10px;
    color: #e1e1e1;
    font-weight: 500;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}

.music-library-item-meta {
    font-size: 9px;
    color: var(--text-muted);
    display: flex;
    gap: 8px;
}

.music-library-item-actions {
    display: flex;
    gap: 4px;
}

.music-library-item-actions .btn {
    padding: 4px 6px;
    font-size: 9px;
}

/* Music Status */
.music-status {
    font-size: 10px;
    padding: 4px 0;
    min-height: 18px;
}

.music-status-info {
    color: var(--accent-primary);
}

.music-status-success {
    color: #28a745;
}

.music-status-error {
    color: #dc3545;
}

/* Music Progress */
.music-progress {
    margin: 10px 0;
}

.music-progress.hidden {
    display: none;
}

.music-progress-bar-container {
    height: 4px;
    background: #333;
    border-radius: 2px;
    overflow: hidden;
    margin-bottom: 6px;
}

.music-progress-bar {
    height: 100%;
    background: var(--accent-primary);
    transition: width 0.3s ease;
}

.music-progress-text {
    font-size: 10px;
    color: var(--text-muted);
    text-align: center;
}

/* Music Variations Panel */
.music-variations-panel {
    margin: 10px 0;
    padding: 10px;
    background: var(--bg-panel);
    border-radius: 4px;
}

.music-variations-panel.hidden {
    display: none;
}

.music-variations-panel>label {
    display: block;
    font-size: 10px;
    color: var(--text-muted);
    margin-bottom: 8px;
}

.music-variations-list {
    display: flex;
    flex-direction: column;
    gap: 6px;
}

.music-variation-card {
    display: flex;
    align-items: center;
    gap: 8px;
    padding: 8px;
    background: var(--bg-card);
    border: 2px solid transparent;
    border-radius: 4px;
    cursor: pointer;
    transition: all 0.2s;
}

.music-variation-card:hover {
    border-color: #555;
}

.music-variation-card.selected {
    border-color: var(--accent-primary);
}

.variation-label {
    font-size: 11px;
    font-weight: 600;
    color: #e1e1e1;
    min-width: 20px;
}

.variation-desc {
    flex: 1;
    font-size: 9px;
    color: var(--text-muted);
}

.variation-play-btn {
    padding: 4px 8px;
    font-size: 9px;
}

/* Music Preview */
.music-preview {
    margin: 10px 0;
    padding: 10px;
    background: var(--bg-panel);
    border-radius: 4px;
}

.music-preview.hidden {
    display: none;
}

.music-preview-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 8px;
}

.music-preview-header span {
    font-size: 10px;
    color: #aaa;
}

#music-audio-player {
    width: 100%;
    height: 30px;
    margin-bottom: 8px;
}

.music-preview-actions {
    display: flex;
    gap: 6px;
}

.music-preview-actions .btn {
    flex: 1;
}

/* Music Align Modal */
.music-align-modal,
.music-timeline-modal {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.7);
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 1000;
}

.music-align-modal.hidden,
.music-timeline-modal.hidden {
    display: none;
}

.music-align-content,
.music-timeline-content {
    background: var(--bg-card);
    border-radius: 8px;
    width: 90%;
    max-width: 280px;
    padding: 16px;
    border: 1px solid var(--glass-border);
}

.music-align-header,
.music-timeline-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 12px;
}

.music-align-header span,
.music-timeline-header span {
    font-size: 12px;
    font-weight: 600;
    color: #e1e1e1;
}

.music-align-info,
.music-timeline-info {
    font-size: 10px;
    color: var(--text-muted);
    margin-bottom: 12px;
    padding: 8px;
    background: var(--bg-panel);
    border-radius: 4px;
}

.music-align-info .info-row {
    display: flex;
    justify-content: space-between;
    margin-bottom: 4px;
}

.music-align-info .info-label {
    color: #666;
}

.music-align-info .info-value {
    color: #e1e1e1;
}

.music-align-actions,
.music-timeline-actions {
    margin-top: 12px;
}

.music-align-actions .btn,
.music-timeline-actions .btn {
    width: 100%;
}

/* Hidden utility */
.hidden {
    display: none !important;
}

/* Collapsed utility */
.collapsed {
    display: none !important;
}

/* Scrollbar */
::-webkit-scrollbar {
    width: 6px;
}

::-webkit-scrollbar-track {
    background: var(--bg-panel);
}

::-webkit-scrollbar-thumb {
    background: var(--glass-border);
    border-radius: 3px;
}

::-webkit-scrollbar-thumb:hover {
    background: #555;
}

