"use client";

import { Card, CardContent } from "@/components/ui/card";
import { motion } from "framer-motion";
import {
  VolumeX,
  Layers,
  Music,
  MessageSquare,
  Scissors,
  Smartphone,
  Check,
  X,
} from "lucide-react";

const features = [
  {
    icon: VolumeX,
    title: "Silence Removal",
    description:
      "Nondestructive text-based editing. Instantly detect and cut pauses and filler words with frame-accurate precision.",
    stats: "Save 3+ hours per edit",
  },
  {
