            {/* Floating UI mockup - Right side */}
            <motion.div
              className="absolute right-0 bottom-8 -mr-6 bg-[#0d1829]/95 backdrop-blur-md rounded-xl p-4 shadow-2xl border border-[#00D4FF]/20 hidden lg:block z-20"
              animate={{ y: [0, -10, 0] }}
              transition={{ duration: 4, repeat: Infinity, ease: "easeInOut", delay: 1 }}
            >
              <div className="flex items-center gap-4">
