   499→
   500→    if (overlaps) {
   501→      protected_.push(silence);
   502→    } else {
   503→      safe.push(silence);
   504→    }
   505→  });
   506→
   507→  console.log(`[SPLICE] Filtered silences: ${safe.length} safe, ${protected_.length} protected`);
   508→  return { safe, protected: protected_ };
   509→}
