  1329→
  1330→// 1. ROBUST CONSTANTS (Fixes 'undefined' error)
  1331→// We define these integers explicitly because UXP API versions vary on where they store Enums.
  1332→const ProjectItemType = {
  1333→    CLIP: 1,
  1334→    BIN: 2,
  1335→    ROOT: 3,
  1336→    FILE: 4
  1337→};
  1338→
  1339→/**
  1340→ * Find the first media item in the project bin (recursively)
  1341→ * @param {Object} item - ProjectItem (usually rootItem)
  1342→ * @returns {Promise<Object|null>} First media item found or null
  1343→ */
  1344→async function findFirstMediaItem(item) {
  1345→  if (!item) return null;
  1346→
  1347→  // Fix: Use local Constant instead of app.Constants.ProjectItemType
  1348→  if (item.type === ProjectItemType.CLIP) {
  1349→    return item;
  1350→  }
  1351→
  1352→  // Search Children (Bin or Root)
  1353→  // Note: item.children is a collection, check numItems
  1354→  if (item.children && item.children.numItems > 0) {
  1355→    for (let i = 0; i < item.children.numItems; i++) {
  1356→      const child = item.children[i];
  1357→      const found = await findFirstMediaItem(child);
  1358→      if (found) return found;
  1359→    }
  1360→  }
  1361→
  1362→  return null;
  1363→}
