  1365→/**
  1366→ * Build a sequence from detection data locally
  1367→ * Used for testing via console: await buildSequenceV35(silences)
  1368→ */
  1369→async function buildSequenceV35(silences, takes = []) {
  1370→    if (!window.spliceBuilder) {
  1371→        throw new Error('Builder not initialized. Check if builder.js is loaded.');
