  1390→/**
  1391→ * Request a cut list from the backend and build the sequence.
  1392→ * Full v3.5 workflow.
  1393→ */
  1394→async function buildSequenceWithCutList() {
  1395→  if (!window.spliceBuilder) {
  1396→    throw new Error('Builder not initialized');
  1397→  }
  1398→
  1399→  // Ensure BACKEND_URL is available (use 127.0.0.1, not localhost - UXP requirement)
  1400→  const apiUrl = (typeof BACKEND_URL !== 'undefined') ? BACKEND_URL : 'https://127.0.0.1:3847';
  1401→
  1402→  if (typeof showProgress === 'function') showProgress('Getting source clip info...');
  1403→
  1404→  const app = require('premierepro');
  1405→  const project = await app.Project.getActiveProject();
  1406→  if (!project) throw new Error("No active project.");
  1407→
  1408→  const rootItem = await project.getRootItem();
  1409→  const sourceProjectItem = await findFirstMediaItem(rootItem);
  1410→
  1411→  if (!sourceProjectItem) {
  1412→    throw new Error('No media items found in project. Please import a media file first.');
  1413→  }
  1414→
  1415→  // Use properties strictly
  1416→  const sourceName = sourceProjectItem.name;
  1417→  const sourcePath = sourceProjectItem.treePath;
  1418→
  1419→  const durationObj = await sourceProjectItem.getDuration();
  1420→  const duration = durationObj ? durationObj.seconds : 0;
  1421→
  1422→  // Retrieve Globals
  1423→  const silences = window.splicePreviewState?.getSelectedSilences() || (typeof safeSilences !== 'undefined' ? safeSilences : []);
  1424→  const takes = (typeof previewTakes !== 'undefined' ? previewTakes : []);
  1425→
  1426→  if (silences.length === 0 && (!takes || takes.length === 0)) {
  1427→     throw new Error('No detection data found. Run analysis first.');
  1428→  }
  1429→
  1430→  if (typeof showProgress === 'function') showProgress('Generating cut list...');
  1431→
  1432→  const response = await fetch(`${apiUrl}/cut-list`, {
  1433→    method: 'POST',
  1434→    headers: getAuthHeaders(),
  1435→    body: JSON.stringify({
  1436→      sourceName,
  1437→      sourcePath,
  1438→      duration,
  1439→      silences,
  1440→      takes
  1441→    })
  1442→  });
  1443→
  1444→  if (!response.ok) {
  1445→    const err = await response.json();
  1446→    throw new Error(err.error || 'Failed to generate cut list');
  1447→  }
  1448→
  1449→  const data = await response.json();
  1450→  const cutList = data.cutList;
  1451→
  1452→  if (typeof showProgress === 'function') showProgress('Building sequence...');
  1453→
  1454→  const result = await window.spliceBuilder.buildSequenceFromCutList(cutList, sourceProjectItem);
  1455→
  1456→  if (!result.success) {
  1457→    throw new Error(result.error || 'Failed to build sequence');
  1458→  }
  1459→
  1460→  return {
  1461→    success: true,
  1462→    clipsAdded: result.clipsAdded,
  1463→    metadata: cutList.metadata
  1464→  };
  1465→}
  1466→
  1467→// Expose functions globally for UI and testing
  1468→if (typeof window !== 'undefined') {
  1469→    window.buildSequenceV35 = buildSequenceV35;
  1470→    window.buildSequenceWithCutList = buildSequenceWithCutList;
  1471→    window.findFirstMediaItem = findFirstMediaItem;
  1472→}
