  1477→async function seekToTime(seconds) {
  1478→  // Validate input
  1479→  if (typeof seconds !== 'number' || isNaN(seconds) || !isFinite(seconds)) {
  1480→    setStatus('Invalid seek time');
  1481→    return;
  1482→  }
  1483→
  1484→  if (seconds < 0) {
  1485→    seconds = 0;
  1486→  }
  1487→
  1488→  try {
  1489→    const context = await getActiveSequence();
  1490→    if (!context) {
  1491→      setStatus('No sequence available');
  1492→      return;
  1493→    }
  1494→
  1495→    const { sequence } = context;
  1496→
  1497→    // Use TickTime API to convert seconds to the format needed
  1498→    const position = ppro.TickTime.createWithSeconds(seconds);
  1499→
  1500→    await sequence.setPlayerPosition(position);
  1501→    setStatus(`Seeked to ${formatTime(seconds)}`);
  1502→  } catch (err) {
  1503→    console.error('[SPLICE] Seek error:', err);
  1504→    setStatus('Seek failed: ' + (err.message || 'Unknown error'));
  1505→  }
  1506→}
