  1622→let batchQueue = [];
  1623→let currentBatchJobId = null;
  1624→
  1625→/**
  1626→ * Initialize batch processing handlers
  1627→ */
  1628→function initBatchHandlers() {
  1629→  const addToBatchBtn = document.getElementById('addToBatchBtn');
  1630→  const startBatchBtn = document.getElementById('startBatchBtn');
  1631→  const clearBatchBtn = document.getElementById('clearBatchBtn');
  1632→  const batchSection = document.getElementById('batchSection');
  1633→
  1634→  // Show batch section in settings or after first detection
  1635→  if (batchSection) {
  1636→    batchSection.classList.remove('hidden');
  1637→  }
  1638→
  1639→  // Add files to batch queue
  1640→  if (addToBatchBtn) {
  1641→    addToBatchBtn.addEventListener('click', async () => {
  1642→      try {
  1643→        const uxpFs = require('uxp').storage.localFileSystem;
  1644→        const files = await uxpFs.getFileForOpening({
  1645→          allowMultiple: true,
  1646→          types: ['wav', 'mp3', 'mp4', 'mov', 'aac', 'm4a']
  1647→        });
  1648→
  1649→        if (files && files.length > 0) {
  1650→          for (const file of files) {
  1651→            const path = file.nativePath || file.name;
  1652→            if (!batchQueue.includes(path)) {
  1653→              batchQueue.push(path);
  1654→            }
  1655→          }
  1656→          updateBatchQueueDisplay();
  1657→          setStatus(`Added ${files.length} file(s) to batch queue`);
  1658→        }
  1659→      } catch (err) {
  1660→        setStatus('Error adding files: ' + err.message);
  1661→      }
  1662→    });
  1663→  }
  1664→
  1665→  // Start batch processing
  1666→  if (startBatchBtn) {
  1667→    startBatchBtn.addEventListener('click', async () => {
  1668→      if (batchQueue.length === 0) {
  1669→        setStatus('No files in batch queue');
  1670→        return;
  1671→      }
  1672→
  1673→      startBatchBtn.disabled = true;
  1674→      addToBatchBtn.disabled = true;
  1675→
  1676→      try {
  1677→        await runBatchProcessing();
  1678→      } finally {
  1679→        startBatchBtn.disabled = false;
  1680→        addToBatchBtn.disabled = false;
  1681→      }
  1682→    });
  1683→  }
  1684→
  1685→  // Clear batch queue
  1686→  if (clearBatchBtn) {
  1687→    clearBatchBtn.addEventListener('click', () => {
