  1748→/**
  1749→ * Run batch processing
  1750→ */
  1751→async function runBatchProcessing() {
  1752→  const batchProgress = document.getElementById('batchProgress');
  1753→  const batchProgressBar = document.getElementById('batchProgressBar');
  1754→  const batchProgressText = document.getElementById('batchProgressText');
  1755→
  1756→  if (batchProgress) batchProgress.style.display = 'block';
  1757→
  1758→  try {
  1759→    // Get settings
  1760→    const sensitivity = parseInt(document.getElementById('sensitivitySlider')?.value || 50);
  1761→    const params = mapSensitivity(sensitivity);
  1762→
  1763→    // Call batch endpoint
  1764→    const response = await fetch(`${BACKEND_URL}/batch/silences`, {
  1765→      method: 'POST',
  1766→      headers: getAuthHeaders(),
  1767→      body: JSON.stringify({
  1768→        files: batchQueue,
  1769→        options: {
  1770→          sensitivity,
  1771→          threshold: params.dbThreshold,
  1772→          minSilenceLength: params.minDuration,
  1773→          paddingStart: params.padding,
  1774→          paddingEnd: params.padding / 2
  1775→        }
  1776→      })
  1777→    });
  1778→
  1779→    if (!response.ok) {
  1780→      const err = await response.json();
  1781→      throw new Error(err.error || 'Batch request failed');
  1782→    }
  1783→
  1784→    const data = await response.json();
  1785→    currentBatchJobId = data.jobId;
  1786→    setStatus(`Batch job started: ${data.jobId}`);
  1787→
  1788→    // Poll for status
  1789→    await pollBatchStatus(currentBatchJobId, batchProgressBar, batchProgressText);
  1790→
  1791→  } catch (err) {
  1792→    setStatus('Batch error: ' + err.message);
  1793→  } finally {
  1794→    if (batchProgress) batchProgress.style.display = 'none';
  1795→  }
  1796→}
  1797→
  1798→/**
  1799→ * Poll batch job status
  1800→ */
  1801→async function pollBatchStatus(jobId, progressBar, progressText) {
  1802→  const pollInterval = 2000; // 2 seconds
  1803→  const maxPolls = 300; // 10 minutes max
  1804→  let polls = 0;
  1805→
  1806→  while (polls < maxPolls) {
  1807→    try {
  1808→      const response = await fetch(`${BACKEND_URL}/batch/status/${jobId}`, {
  1809→        headers: getAuthHeaders()
  1810→      });
  1811→
  1812→      if (!response.ok) {
  1813→        throw new Error('Failed to get batch status');
  1814→      }
  1815→
  1816→      const data = await response.json();
  1817→      const job = data.job;
  1818→
  1819→      // Update progress
  1820→      if (progressBar) progressBar.value = job.progress.percentage;
  1821→      if (progressText) progressText.textContent = `${job.progress.completed + job.progress.failed} / ${job.progress.total} files`;
  1822→
  1823→      // Check if complete
  1824→      if (job.status !== 'processing') {
  1825→        if (job.status === 'completed') {
  1826→          setStatus(`Batch complete: ${job.progress.completed} files processed`);
  1827→        } else if (job.status === 'completed_with_errors') {
  1828→          setStatus(`Batch complete with ${job.progress.failed} error(s)`);
  1829→        } else {
  1830→          setStatus('Batch failed');
  1831→        }
  1832→
  1833→        // Clear queue on completion
  1834→        batchQueue = [];
  1835→        updateBatchQueueDisplay();
  1836→        return;
  1837→      }
  1838→
  1839→      await new Promise(resolve => setTimeout(resolve, pollInterval));
  1840→      polls++;
  1841→    } catch (err) {
  1842→      setStatus('Polling error: ' + err.message);
  1843→      return;
  1844→    }
  1845→  }
  1846→
  1847→  setStatus('Batch timed out');
  1848→}
  1849→
