    84→  const context = await getActiveSequence();
    85→  if (!context) {
    86→    throw new Error('No project or sequence open');
    87→  }
    88→
    89→  const { project, sequence } = context;
    90→
    91→  // Get sequence info for manual export instructions
    92→  const sequenceName = await sequence.getName();
    93→
    94→  // UXP doesn't have exportFinalCutProXML yet
