   141→
   142→/**
   143→ * Initialize Razor button
   144→ */
   145→function initRazorButton() {
   146→  const btn = document.getElementById('razorSilencesBtn');
   147→  if (!btn) return;
   148→
   149→  btn.addEventListener('click', async () => {
   150→    // Check for preview-selected silences first, fall back to global
