 * v3.4 - Timeline Markers + Unified Take-Aware Workflow.
 */

// Initialize Premiere Pro API
const ppro = require('premierepro');

// ============================================================================
// CACHED DOM ELEMENTS (Performance: avoid repeated getElementById calls)
// ============================================================================
const ui = {};

function cacheUIElements() {
