    await nodeFetchWithTimeout(`${getBackendUrl()}/health`, {}, 1);
    throw new Error('Should have timed out');
  } catch (err) {
    if (!err.message.includes('timed out') && err.name !== 'AbortError') {
      throw new Error(`Expected timeout error, got: ${err.message}`);
    }
  }
});

