     1→/**
     2→ * Music Routes
     3→ *
     4→ * AI Music generation endpoints (Phase 8)
     5→ */
     6→
     7→const express = require('express');
     8→
     9→// Lazy-load music services to prevent startup failures if deps missing
    10→let musicQueue, songIdentification, musicGeneration, r2Storage, musicAlignment, musicTimeline;
    11→
    12→function loadMusicServices() {
    13→  if (!musicQueue) {
    14→    try {
    15→      musicQueue = require('../services/musicQueue');
    16→      songIdentification = require('../services/songIdentification');
    17→      musicGeneration = require('../services/musicGeneration');
    18→      r2Storage = require('../services/r2Storage');
    19→      musicAlignment = require('../services/musicAlignment');
    20→      musicTimeline = require('../services/musicTimeline');
    21→    } catch (err) {
    22→      console.warn('[SPLICE] Music services not available:', err.message);
    23→    }
    24→  }
    25→}
    26→
    27→/**
    28→ * Create music routes
    29→ * @param {Object} options - Route configuration options
    30→ * @param {Object} options.middleware - Shared middleware (requireCredits)
    31→ * @param {Object} options.services - Shared services (usageTracking)
    32→ * @returns {express.Router}
    33→ */
    34→function createMusicRoutes(options = {}) {
    35→  const router = express.Router();
    36→  const { requireCredits } = options.middleware || {};
    37→  const { usageTracking } = options.services || {};
    38→
    39→  // Load services on first route access
    40→  loadMusicServices();
    41→
    42→  /**
    43→   * POST /identify - Identify song from YouTube URL
    44→   */
    45→  router.post('/identify', requireCredits, async (req, res) => {
    46→    const { youtubeUrl } = req.body;
    47→
    48→    if (!youtubeUrl) {
    49→      return res.status(400).json({ error: 'youtubeUrl is required' });
    50→    }
    51→
    52→    if (!songIdentification) {
    53→      return res.status(503).json({ error: 'Song identification service not available' });
    54→    }
    55→
    56→    try {
    57→      // Validate URL
    58→      const validation = songIdentification.validateYouTubeUrl(youtubeUrl);
    59→      if (!validation.valid) {
    60→        return res.status(400).json({ error: validation.error });
    61→      }
    62→
    63→      // Check dependencies
    64→      const deps = await songIdentification.checkDependencies();
    65→      if (!deps.ytdlp) {
    66→        return res.status(503).json({ error: 'yt-dlp is not installed on server' });
    67→      }
    68→      if (!deps.ffmpeg) {
    69→        return res.status(503).json({ error: 'FFmpeg is not installed on server' });
    70→      }
    71→
    72→      // Check ACRCloud credentials
    73→      if (!songIdentification.hasACRCloudCredentials()) {
    74→        return res.status(503).json({ error: 'ACRCloud credentials not configured' });
    75→      }
    76→
    77→      // Identify song
    78→      const result = await songIdentification.identifyFromYouTube(youtubeUrl);
    79→
    80→      res.json(result);
    81→
    82→    } catch (err) {
    83→      console.error('[SPLICE] Song identification error:', err.message);
    84→      res.status(500).json({ error: err.message || 'Song identification failed' });
    85→    }
    86→  });
    87→
    88→  /**
    89→   * POST /generate - Start music generation job
    90→   */
    91→  router.post('/generate', requireCredits, async (req, res) => {
    92→    const { youtubeUrl, prompt, duration, mood, instruments, referenceSong } = req.body;
    93→    const customerId = req.stripeCustomerId;
    94→
    95→    if (!customerId) {
    96→      return res.status(401).json({ error: 'Authentication required' });
    97→    }
    98→
    99→    if (!musicQueue) {
   100→      return res.status(503).json({ error: 'Music generation service not available' });
   101→    }
   102→
   103→    try {
   104→      // Validate options if musicGeneration is available
   105→      if (musicGeneration) {
   106→        const validation = musicGeneration.validateOptions({ duration, mood, instruments });
   107→        if (!validation.valid) {
   108→          return res.status(400).json({ error: validation.errors.join(', ') });
   109→        }
   110→      }
   111→
   112→      // Add job to queue
   113→      const result = await musicQueue.addMusicJob({
   114→        customerId,
   115→        youtubeUrl,
   116→        prompt,
   117→        duration: duration || 60,
   118→        mood: mood || 'neutral',
   119→        instruments: instruments || [],
   120→        referenceSong
   121→      });
   122→
   123→      res.json({
   124→        success: true,
   125→        jobId: result.jobId,
   126→        status: result.status,
   127→        estimatedTime: result.estimatedTime,
   128→        statusUrl: `/music/status/${result.jobId}`
   129→      });
   130→
   131→    } catch (err) {
   132→      console.error('[SPLICE] Music generation error:', err.message);
   133→      res.status(500).json({ error: err.message || 'Failed to start music generation' });
   134→    }
   135→  });
   136→
   137→  /**
   138→   * POST /generate-variations - Generate 3 song variations for user selection
   139→   */
   140→  router.post('/generate-variations', requireCredits, async (req, res) => {
   141→    const { prompt, duration, mood, instruments, referenceSong } = req.body;
   142→    const customerId = req.stripeCustomerId;
   143→
   144→    if (!customerId) {
   145→      return res.status(401).json({ error: 'Authentication required' });
   146→    }
   147→
   148→    if (!musicQueue) {
   149→      return res.status(503).json({ error: 'Music generation service not available' });
   150→    }
   151→
   152→    try {
   153→      // Validate options if musicGeneration is available
   154→      if (musicGeneration) {
   155→        const validation = musicGeneration.validateOptions({ duration, mood, instruments });
   156→        if (!validation.valid) {
   157→          return res.status(400).json({ error: validation.errors.join(', ') });
   158→        }
   159→      }
   160→
   161→      // Check for variations credits (2.5 credits = 3 music credits equivalent)
   162→      const hasCredits = await usageTracking.checkVariationsCredits(customerId);
   163→      if (!hasCredits.canGenerate) {
   164→        return res.status(402).json({
   165→          error: 'Insufficient credits for variations',
   166→          creditsRequired: 2.5,
   167→          creditsAvailable: hasCredits.creditsAvailable
   168→        });
   169→      }
   170→
   171→      // Add variations job to queue
   172→      const result = await musicQueue.addVariationsJob({
   173→        customerId,
   174→        prompt,
   175→        duration: duration || 60,
   176→        mood: mood || 'neutral',
   177→        instruments: instruments || [],
   178→        referenceSong
   179→      });
   180→
   181→      res.json({
   182→        success: true,
   183→        jobId: result.jobId,
   184→        status: result.status,
   185→        isVariations: true,
   186→        estimatedTime: result.estimatedTime,
   187→        statusUrl: `/music/variations/status/${result.jobId}`
   188→      });
   189→
   190→    } catch (err) {
   191→      console.error('[SPLICE] Music variations error:', err.message);
   192→      res.status(500).json({ error: err.message || 'Failed to start variations generation' });
   193→    }
   194→  });
   195→
   196→  /**
   197→   * POST /generate-scene-aware - Generate music with transcript analysis
   198→   */
   199→  router.post('/generate-scene-aware', requireCredits, async (req, res) => {
   200→    const { segments, prompt, duration, mood, instruments, referenceSong } = req.body;
   201→    const customerId = req.stripeCustomerId;
   202→
   203→    if (!customerId) {
   204→      return res.status(401).json({ error: 'Authentication required' });
   205→    }
   206→
   207→    if (!musicQueue) {
   208→      return res.status(503).json({ error: 'Music generation service not available' });
   209→    }
   210→
   211→    // Validate segments
   212→    if (!segments || !Array.isArray(segments) || segments.length === 0) {
   213→      return res.status(400).json({ error: 'Transcript segments are required' });
   214→    }
   215→
   216→    try {
   217→      // Validate scene-aware options
   218→      if (musicGeneration) {
   219→        const validation = musicGeneration.validateSceneAwareOptions(segments, { duration, mood, instruments });
   220→        if (!validation.valid) {
   221→          return res.status(400).json({ error: validation.errors.join(', ') });
   222→        }
   223→      }
   224→
   225→      // Check for scene-aware credits (1.5 credits)
   226→      const hasCredits = await usageTracking.checkSceneAwareCredits(customerId);
   227→      if (!hasCredits.canGenerate) {
   228→        return res.status(402).json({
   229→          error: 'Insufficient credits for scene-aware music',
   230→          creditsRequired: 1.5,
   231→          creditsAvailable: hasCredits.creditsAvailable
   232→        });
   233→      }
   234→
   235→      // Add scene-aware job to queue with scene analysis flag
   236→      const result = await musicQueue.addMusicJob({
   237→        customerId,
   238→        prompt,
   239→        duration: duration || 60,
   240→        mood: mood || 'neutral',
   241→        instruments: instruments || [],
   242→        referenceSong,
   243→        isSceneAware: true,
   244→        segments
   245→      });
   246→
   247→      res.json({
   248→        success: true,
   249→        jobId: result.jobId,
   250→        status: result.status,
   251→        isSceneAware: true,
   252→        estimatedTime: result.estimatedTime,
   253→        statusUrl: `/music/status/${result.jobId}`
   254→      });
   255→
   256→    } catch (err) {
   257→      console.error('[SPLICE] Scene-aware music error:', err.message);
   258→      res.status(500).json({ error: err.message || 'Failed to start scene-aware music generation' });
   259→    }
   260→  });
   261→
   262→  /**
   263→   * GET /variations/status/:jobId - Get variations job status with detailed progress
   264→   */
   265→  router.get('/variations/status/:jobId', requireCredits, async (req, res) => {
   266→    const { jobId } = req.params;
   267→    const customerId = req.stripeCustomerId;
   268→
   269→    if (!musicQueue) {
   270→      return res.status(503).json({ error: 'Music generation service not available' });
   271→    }
   272→
   273→    try {
   274→      const status = await musicQueue.getVariationsJobStatus(jobId);
   275→
   276→      if (!status) {
   277→        return res.status(404).json({ error: 'Job not found' });
   278→      }
   279→
   280→      // Verify ownership
   281→      if (status.data.customerId !== customerId) {
   282→        return res.status(403).json({ error: 'Not authorized to access this job' });
   283→      }
   284→
   285→      res.json(status);
   286→
   287→    } catch (err) {
   288→      console.error('[SPLICE] Variations status error:', err.message);
   289→      res.status(500).json({ error: err.message || 'Failed to get job status' });
   290→    }
   291→  });
   292→
   293→  /**
   294→   * POST /variations/:jobId/select - Select a variation to finalize
   295→   */
   296→  router.post('/variations/:jobId/select', requireCredits, async (req, res) => {
   297→    const { jobId } = req.params;
   298→    const { variationIndex } = req.body;
   299→    const customerId = req.stripeCustomerId;
   300→
   301→    if (!musicQueue) {
   302→      return res.status(503).json({ error: 'Music generation service not available' });
   303→    }
   304→
   305→    if (variationIndex === undefined || variationIndex === null) {
   306→      return res.status(400).json({ error: 'variationIndex is required' });
   307→    }
   308→
   309→    const index = parseInt(variationIndex);
   310→    if (isNaN(index) || index < 0 || index > 2) {
   311→      return res.status(400).json({ error: 'variationIndex must be 0, 1, or 2' });
   312→    }
   313→
   314→    try {
   315→      // Select the variation
   316→      const result = await musicQueue.selectVariation(jobId, index, customerId);
   317→
   318→      // Deduct variations credits (2.5 credits)
   319→      await usageTracking.deductVariationsCredit(customerId);
   320→
   321→      res.json({
   322→        success: true,
   323→        ...result
   324→      });
   325→
   326→    } catch (err) {
   327→      console.error('[SPLICE] Variation selection error:', err.message);
   328→      res.status(500).json({ error: err.message || 'Failed to select variation' });
   329→    }
   330→  });
   331→
   332→  /**
   333→   * GET /status/:jobId - Get job status
   334→   */
   335→  router.get('/status/:jobId', requireCredits, async (req, res) => {
   336→    const { jobId } = req.params;
