   343→    try {
   344→      const status = await musicQueue.getJobStatus(jobId);
   345→
   346→      if (!status) {
   347→        return res.status(404).json({ error: 'Job not found' });
   348→      }
   349→
   350→      // Verify ownership
   351→      if (status.data.customerId !== customerId) {
   352→        return res.status(403).json({ error: 'Access denied' });
   353→      }
   354→
   355→      res.json(status);
   356→
   357→    } catch (err) {
