   363→  /**
   364→   * GET /library - Get user's music library
   365→   */
   366→  router.get('/library', requireCredits, async (req, res) => {
   367→    const customerId = req.stripeCustomerId;
   368→
   369→    if (!customerId) {
   370→      return res.status(401).json({ error: 'Authentication required' });
   371→    }
   372→
   373→    if (!musicQueue) {
   374→      return res.status(503).json({ error: 'Music generation service not available' });
   375→    }
   376→
   377→    try {
   378→      const jobs = await musicQueue.getCustomerJobs(customerId, {
   379→        limit: parseInt(req.query.limit) || 50,
   380→        offset: parseInt(req.query.offset) || 0
   381→      });
   382→
   383→      // Filter to only completed jobs for library
   384→      const library = jobs.filter(j => j.status === 'completed');
   385→
   386→      res.json(library);
   387→
   388→    } catch (err) {
   389→      console.error('[SPLICE] Music library error:', err.message);
   390→      res.status(500).json({ error: err.message || 'Failed to load music library' });
   391→    }
   392→  });
