   394→  /**
   395→   * GET /:jobId - Get music with signed download URL
   396→   */
   397→  router.get('/:jobId', requireCredits, async (req, res) => {
   398→    const { jobId } = req.params;
   399→    const customerId = req.stripeCustomerId;
   400→
   401→    if (!musicQueue || !r2Storage) {
   402→      return res.status(503).json({ error: 'Music service not available' });
   403→    }
   404→
   405→    try {
   406→      const status = await musicQueue.getJobStatus(jobId);
   407→
   408→      if (!status) {
   409→        return res.status(404).json({ error: 'Music not found' });
   410→      }
   411→
   412→      // Verify ownership
   413→      if (status.data.customerId !== customerId) {
   414→        return res.status(403).json({ error: 'Access denied' });
   415→      }
   416→
   417→      if (status.status !== 'completed') {
   418→        return res.status(400).json({ error: 'Music generation not complete' });
   419→      }
   420→
   421→      // Generate signed download URLs
   422→      const masterKey = r2Storage.generateStorageKey(customerId, jobId, 'master.wav');
   423→      const previewKey = r2Storage.generateStorageKey(customerId, jobId, 'preview.mp3');
   424→
   425→      const [downloadUrl, previewUrl] = await Promise.all([
   426→        r2Storage.getSignedDownloadUrl(masterKey, 3600), // 1 hour
   427→        r2Storage.getSignedDownloadUrl(previewKey, 3600).catch(() => null)
   428→      ]);
   429→
   430→      res.json({
   431→        success: true,
   432→        music: {
   433→          jobId,
   434→          title: status.data.musicResult?.title || 'Generated Music',
   435→          duration: status.data.duration,
   436→          mood: status.data.mood,
   437→          downloadUrl,
   438→          previewUrl,
   439→          createdAt: status.createdAt,
   440→          completedAt: status.completedAt
   441→        }
   442→      });
   443→
   444→    } catch (err) {
   445→      console.error('[SPLICE] Get music error:', err.message);
   446→      res.status(500).json({ error: err.message || 'Failed to get music' });
   447→    }
   448→  });
