   450→  /**
   451→   * DELETE /:jobId - Delete music from library
   452→   */
   453→  router.delete('/:jobId', requireCredits, async (req, res) => {
   454→    const { jobId } = req.params;
   455→    const customerId = req.stripeCustomerId;
   456→
   457→    if (!musicQueue || !r2Storage) {
   458→      return res.status(503).json({ error: 'Music service not available' });
   459→    }
   460→
   461→    try {
   462→      const status = await musicQueue.getJobStatus(jobId);
   463→
   464→      if (!status) {
   465→        return res.status(404).json({ error: 'Music not found' });
   466→      }
   467→
   468→      // Verify ownership
   469→      if (status.data.customerId !== customerId) {
   470→        return res.status(403).json({ error: 'Access denied' });
   471→      }
   472→
   473→      // Delete from R2
   474→      await r2Storage.deleteJobFiles(customerId, jobId);
   475→
   476→      // Note: Job record stays in Redis for history
   477→      // Could optionally call musicQueue.cancelJob if we want to remove it
   478→
   479→      res.json({ success: true, message: 'Music deleted' });
   480→
   481→    } catch (err) {
   482→      console.error('[SPLICE] Delete music error:', err.message);
   483→      res.status(500).json({ error: err.message || 'Failed to delete music' });
   484→    }
   485→  });
