   533→  /**
   534→   * GET /queue-stats - Get queue statistics (admin only)
   535→   */
   536→  router.get('/queue-stats', requireCredits, async (req, res) => {
   537→    if (!musicQueue) {
   538→      return res.status(503).json({ error: 'Music service not available' });
   539→    }
   540→
   541→    try {
   542→      const stats = await musicQueue.getQueueStats();
