   550→  /**
   551→   * POST /align - Align music to video duration with beat-matching
   552→   */
   553→  router.post('/align', requireCredits, async (req, res) => {
   554→    const { jobId, targetDuration, fadeDuration, beatAlign = true, searchWindow } = req.body;
   555→    const customerId = req.stripeCustomerId;
   556→
   557→    if (!jobId) {
   558→      return res.status(400).json({ error: 'jobId is required' });
   559→    }
   560→
   561→    if (!targetDuration || typeof targetDuration !== 'number') {
   562→      return res.status(400).json({ error: 'targetDuration (number in seconds) is required' });
   563→    }
   564→
   565→    if (!musicAlignment || !musicQueue || !r2Storage) {
   566→      return res.status(503).json({ error: 'Music alignment service not available' });
   567→    }
   568→
   569→    try {
   570→      // Validate options
   571→      const validation = musicAlignment.validateAlignmentOptions({
   572→        targetDuration,
   573→        fadeDuration,
   574→        searchWindow
   575→      });
   576→      if (!validation.valid) {
   577→        return res.status(400).json({ error: validation.errors.join(', ') });
   578→      }
   579→
   580→      // Get job and verify ownership
   581→      const job = await musicQueue.getJobStatus(jobId);
   582→      if (!job) {
   583→        return res.status(404).json({ error: 'Job not found' });
   584→      }
   585→      if (job.customerId !== customerId) {
   586→        return res.status(403).json({ error: 'Access denied' });
   587→      }
   588→      if (job.status !== 'completed') {
   589→        return res.status(400).json({ error: 'Job must be completed before alignment' });
   590→      }
   591→
   592→      // Download the master audio from R2
   593→      const masterKey = r2Storage.generateStorageKey(customerId, jobId, 'master.wav');
   594→      const audioBuffer = await r2Storage.downloadFile(masterKey);
   595→
   596→      // Perform alignment with beat detection
   597→      const result = await musicAlignment.trimToLength(audioBuffer, targetDuration, {
   598→        fadeDuration: fadeDuration || musicAlignment.DEFAULT_FADE_DURATION,
   599→        beatAlign,
   600→        searchWindow: searchWindow || musicAlignment.BEAT_SEARCH_WINDOW
   601→      });
   602→
   603→      // Generate aligned file key
   604→      const alignedKey = r2Storage.generateStorageKey(customerId, jobId, `aligned_${Math.round(targetDuration)}s.wav`);
   605→
   606→      // Upload aligned audio to R2
   607→      await r2Storage.uploadBuffer(alignedKey, result.buffer, 'audio/wav');
   608→
   609→      // Get signed URL for download
   610→      const downloadUrl = await r2Storage.getSignedDownloadUrl(alignedKey, 3600);
   611→
   612→      res.json({
   613→        success: true,
   614→        jobId,
   615→        targetDuration,
   616→        actualDuration: result.cutTime,
   617→        wasAligned: result.wasAligned,
   618→        beatCount: result.beats.length,
   619→        downloadUrl,
   620→        alignedKey
   621→      });
   622→
   623→    } catch (err) {
   624→      console.error('[SPLICE] Music align error:', err.message);
   625→      res.status(500).json({ error: err.message || 'Failed to align music' });
   626→    }
   627→  });
