(async () => {
  const BASE = 'https://127.0.0.1:3847';
  const results = [];

  // Test all public GET endpoints
  const endpoints = [
    '/health',
    '/music/moods',
    '/music/instruments', 
    '/music/alignment-options',
    '/music/timeline-options',
    '/captions/templates',
    '/export/formats',
    '/reframe/platforms'
  ];

  for (const ep of endpoints) {
    try {
      const res = await fetch(BASE + ep);
      const data = await res.json();
      results.push({ endpoint: ep, status: res.status, ok: res.ok, keys: Object.keys(data) });
    } catch (e) {
      results.push({ endpoint: ep, error: e.message });
    }
  }

  // Test POST endpoints (should return auth error)
  const postEndpoints = [
    { path: '/silences-rms', body: { wavPath: '/test.wav' } },
    { path: '/chapters', body: { transcript: { segments: [] } } },
    { path: '/isolate-vocals', body: { wavPath: '/test.wav' } }
  ];

  for (const ep of postEndpoints) {
    try {
      const res = await fetch(BASE + ep.path, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(ep.body)
      });
      const data = await res.json();
      results.push({ endpoint: ep.path, status: res.status, authRequired: data.error?.includes('Authentication') || data.message?.includes('Authentication') });
    } catch (e) {
      results.push({ endpoint: ep.path, error: e.message });
    }
  }

  return results;
})()

