   552→/**
   553→ * Ensure we have a valid access token, refreshing if needed
   554→ * @returns {Promise<boolean>} True if we have a valid token
   555→ */
   556→async function ensureValidToken() {
   557→    const settings = getSettings();
   558→
   559→    // No token at all - need to login
