  1532→                debugLog('Message contains "fetch":', err?.message?.toLowerCase()?.includes('fetch'));
  1533→                debugLog('Message contains "network":', err?.message?.toLowerCase()?.includes('network'));
  1534→                debugLog('Message contains "CORS":', err?.message?.toLowerCase()?.includes('cors'));
  1535→                debugLog('Message contains "SSL":', err?.message?.toLowerCase()?.includes('ssl'));
  1536→                debugLog('Message contains "certificate":', err?.message?.toLowerCase()?.includes('certificate'));
  1537→                debugLog('Full error object:', JSON.stringify(err, Object.getOwnPropertyNames(err)));
  1538→                debugLog('================================================');
  1539→
  1540→                // Provide detailed error message based on error type
  1541→                let errorMessage = 'Connection error. ';
