  1787→        const stored = localStorage.getItem(ERROR_LOG_KEY);
  1788→        if (stored) {
  1789→            errorLog = JSON.parse(stored);
  1790→        }
  1791→
  1792→        // Add new error and trim to max size
  1793→        errorLog.unshift(errorEntry);
  1794→        if (errorLog.length > MAX_ERROR_LOG_SIZE) {
  1795→            errorLog = errorLog.slice(0, MAX_ERROR_LOG_SIZE);
  1796→        }
  1797→
  1798→        localStorage.setItem(ERROR_LOG_KEY, JSON.stringify(errorLog));
  1799→    } catch (e) {
  1800→        // Silent fail - localStorage might be full or unavailable
  1801→        console.warn('[SPLICE] Failed to persist error log:', e);
  1802→    }
  1803→
  1804→    return errorEntry;
  1805→}
