 */
async function getActiveSequenceWithRetry(retries = 3, delayMs = 300) {
    console.log('[SPLICE] getActiveSequenceWithRetry: Starting with retries=' + retries);

    for (let attempt = 0; attempt <= retries; attempt++) {
