   573→// ============================================================================
   574→// SETTINGS MANAGEMENT
   575→// ============================================================================
   576→
   577→const DEFAULT_SETTINGS = {
   578→    sensitivity: 50,
   579→    minSilenceLength: 0.5,
   580→    sourceIsolated: false,
   581→    enableTakesDetection: true,
   582→    enableJCut: false,
   583→    jcutLeadIn: 0.3,
   584→    jcutLeadOut: 0.2,
   585→    enableZoom: false,
