   606→
   607→// Current storage version for future migrations
   608→const SETTINGS_STORAGE_VERSION = 2;
   609→
   610→// Fields that contain sensitive data and should be encrypted
   611→const SENSITIVE_FIELDS = ['customerId', 'licenseKey', 'accessToken', 'refreshToken'];
   612→
   613→// ============================================================================
   614→// ENCRYPTION HELPERS FOR SENSITIVE DATA (CEP-CRIT-002 FIX)
   615→// Uses AES-GCM encryption via Web Crypto API for proper security
   616→// ============================================================================
   617→
   618→// Encryption constants
   619→const SPLICE_ENCRYPTION = {
   620→    ALGORITHM: 'AES-GCM',
   621→    KEY_LENGTH: 256,
   622→    IV_LENGTH: 12,
   623→    TAG_LENGTH: 128,
   624→    SALT: 'SPLICE_PANEL_SALT_v1', // Static salt for key derivation
   625→    ITERATIONS: 100000,
   626→    PREFIX: 'ENC:v1:'  // Prefix to identify encrypted values
   627→};
   628→
   629→// Cached encryption key (derived once per session)
   630→let _encryptionKey = null;
   631→
   632→/**
   633→ * Get or derive the encryption key from a machine-specific identifier
   634→ * Uses PBKDF2 to derive a strong key from available identifiers
   635→ * @returns {Promise<CryptoKey>} The derived encryption key
   636→ */
   637→async function getEncryptionKey() {
   638→    if (_encryptionKey) {
   639→        return _encryptionKey;
   640→    }
   641→
   642→    try {
   643→        // Use a combination of identifiers for key derivation
   644→        // In CEP, we use localStorage ID + app version as the key material
   645→        let keyMaterial = localStorage.getItem('splice_device_id');
   646→        if (!keyMaterial) {
   647→            // Generate a random device ID on first use
   648→            keyMaterial = crypto.randomUUID ? crypto.randomUUID() :
   649→                'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function (c) {
   650→                    const r = Math.random() * 16 | 0;
   651→                    const v = c === 'x' ? r : (r & 0x3 | 0x8);
   652→                    return v.toString(16);
   653→                });
   654→            localStorage.setItem('splice_device_id', keyMaterial);
   655→        }
   656→
   657→        const encoder = new TextEncoder();
   658→        const keyData = encoder.encode(keyMaterial + SPLICE_ENCRYPTION.SALT);
   659→
   660→        // Import the key material for PBKDF2
   661→        const baseKey = await crypto.subtle.importKey(
   662→            'raw',
   663→            keyData,
   664→            'PBKDF2',
   665→            false,
   666→            ['deriveKey']
   667→        );
   668→
   669→        // Derive the actual AES key
   670→        _encryptionKey = await crypto.subtle.deriveKey(
   671→            {
   672→                name: 'PBKDF2',
   673→                salt: encoder.encode(SPLICE_ENCRYPTION.SALT),
   674→                iterations: SPLICE_ENCRYPTION.ITERATIONS,
   675→                hash: 'SHA-256'
   676→            },
   677→            baseKey,
   678→            {
   679→                name: SPLICE_ENCRYPTION.ALGORITHM,
   680→                length: SPLICE_ENCRYPTION.KEY_LENGTH
   681→            },
   682→            false,
   683→            ['encrypt', 'decrypt']
   684→        );
   685→
   686→        return _encryptionKey;
   687→    } catch (e) {
   688→        debugLog('[Config] Key derivation error:', e);
   689→        return null;
   690→    }
   691→}
   692→
   693→/**
   694→ * Encrypt data for secure localStorage storage using AES-GCM
   695→ *
   696→ * @param {any} data - Data to encrypt
   697→ * @returns {Promise<string|null>} Encrypted string with prefix or null on error
   698→ */
   699→async function encryptForStorageAsync(data) {
   700→    try {
   701→        if (data === null || data === undefined) {
   702→            return null;
   703→        }
   704→
   705→        const key = await getEncryptionKey();
   706→        if (!key) {
   707→            // Fallback to legacy encoding if crypto not available
   708→            debugLog('[Config] Crypto not available, using legacy encoding');
   709→            return legacyEncode(data);
   710→        }
   711→
   712→        const encoder = new TextEncoder();
   713→        const jsonStr = JSON.stringify(data);
   714→        const plaintext = encoder.encode(jsonStr);
   715→
   716→        // Generate random IV
   717→        const iv = crypto.getRandomValues(new Uint8Array(SPLICE_ENCRYPTION.IV_LENGTH));
   718→
   719→        // Encrypt
   720→        const ciphertext = await crypto.subtle.encrypt(
   721→            {
   722→                name: SPLICE_ENCRYPTION.ALGORITHM,
   723→                iv: iv,
   724→                tagLength: SPLICE_ENCRYPTION.TAG_LENGTH
   725→            },
   726→            key,
   727→            plaintext
   728→        );
   729→
   730→        // Combine IV + ciphertext and encode as base64
   731→        const combined = new Uint8Array(iv.length + ciphertext.byteLength);
   732→        combined.set(iv);
   733→        combined.set(new Uint8Array(ciphertext), iv.length);
   734→
   735→        // Convert to base64 with prefix
   736→        const base64 = btoa(String.fromCharCode(...combined));
   737→        return SPLICE_ENCRYPTION.PREFIX + base64;
   738→    } catch (e) {
   739→        debugLog('[Config] Encryption error:', e);
   740→        return legacyEncode(data);
   741→    }
   742→}
   743→
   744→/**
   745→ * Decrypt data from localStorage using AES-GCM
   746→ *
   747→ * @param {string} data - Encrypted string
   748→ * @returns {Promise<any|null>} Decrypted data or null on error
   749→ */
   750→async function decryptFromStorageAsync(data) {
   751→    try {
   752→        if (!data || typeof data !== 'string') {
   753→            return null;
   754→        }
   755→
   756→        // Check if this is the new encrypted format
   757→        if (!data.startsWith(SPLICE_ENCRYPTION.PREFIX)) {
   758→            // Try legacy decoding for backward compatibility
   759→            return legacyDecode(data);
   760→        }
   761→
   762→        const key = await getEncryptionKey();
   763→        if (!key) {
   764→            debugLog('[Config] Crypto not available for decryption');
   765→            return null;
   766→        }
   767→
   768→        // Remove prefix and decode base64
   769→        const base64Data = data.slice(SPLICE_ENCRYPTION.PREFIX.length);
   770→        const combined = Uint8Array.from(atob(base64Data), c => c.charCodeAt(0));
   771→
   772→        // Extract IV and ciphertext
   773→        const iv = combined.slice(0, SPLICE_ENCRYPTION.IV_LENGTH);
   774→        const ciphertext = combined.slice(SPLICE_ENCRYPTION.IV_LENGTH);
   775→
   776→        // Decrypt
   777→        const plaintext = await crypto.subtle.decrypt(
   778→            {
   779→                name: SPLICE_ENCRYPTION.ALGORITHM,
   780→                iv: iv,
   781→                tagLength: SPLICE_ENCRYPTION.TAG_LENGTH
   782→            },
   783→            key,
   784→            ciphertext
   785→        );
   786→
   787→        const decoder = new TextDecoder();
   788→        const jsonStr = decoder.decode(plaintext);
   789→        return JSON.parse(jsonStr);
   790→    } catch (e) {
   791→        // Try legacy decoding as fallback
   792→        return legacyDecode(data);
   793→    }
   794→}
   795→
   796→/**
   797→ * Legacy encoding (Base64) for backward compatibility
   798→ * @param {any} data - Data to encode
   799→ * @returns {string|null} Encoded string
   800→ */
   801→function legacyEncode(data) {
   802→    try {
   803→        if (data === null || data === undefined) {
   804→            return null;
   805→        }
   806→        const jsonStr = JSON.stringify(data);
   807→        const encoded = encodeURIComponent(jsonStr);
   808→        return btoa(encoded);
   809→    } catch (e) {
   810→        return null;
   811→    }
   812→}
   813→
   814→/**
   815→ * Legacy decoding (Base64) for backward compatibility
   816→ * @param {string} data - Encoded string
   817→ * @returns {any|null} Decoded data
   818→ */
   819→function legacyDecode(data) {
   820→    try {
   821→        if (!data || typeof data !== 'string') {
   822→            return null;
   823→        }
   824→        // Skip if it has the encryption prefix
   825→        if (data.startsWith(SPLICE_ENCRYPTION.PREFIX)) {
   826→            return null;
   827→        }
   828→        const decoded = atob(data);
   829→        const jsonStr = decodeURIComponent(decoded);
   830→        return JSON.parse(jsonStr);
   831→    } catch (e) {
   832→        return null;
   833→    }
   834→}
   835→
   836→/**
   837→ * Synchronous wrapper for encryption (starts async operation)
   838→ * For backward compatibility with existing sync code
   839→ * @param {any} data - Data to encode
   840→ * @returns {string|null} Encoded string (legacy format until async completes)
   841→ */
   842→function encryptForStorage(data) {
   843→    // Return legacy encoding synchronously
   844→    // The async version should be preferred for new code
   845→    return legacyEncode(data);
   846→}
   847→
   848→/**
   849→ * Synchronous wrapper for decryption
   850→ * For backward compatibility with existing sync code
   851→ * @param {string} data - Encoded string
   852→ * @returns {any|null} Decoded data
   853→ */
   854→function decryptFromStorage(data) {
   855→    // Handle new encrypted format by returning null (needs async)
   856→    if (data && data.startsWith(SPLICE_ENCRYPTION.PREFIX)) {
   857→        debugLog('[Config] Encrypted data requires async decryption');
   858→        return null;
   859→    }
   860→    return legacyDecode(data);
   861→}
   862→
   863→/**
   864→ * Check if a value appears to be encrypted
   865→ *
   866→ * @param {any} value - Value to check
   867→ * @returns {boolean} True if value appears to be encrypted
   868→ */
   869→function isEncryptedValue(value) {
   870→    if (!value || typeof value !== 'string') {
   871→        return false;
   872→    }
   873→    // New encryption format
   874→    if (value.startsWith(SPLICE_ENCRYPTION.PREFIX)) {
   875→        return true;
   876→    }
   877→    // Unencrypted values typically start with known patterns
   878→    if (value.startsWith('cus_') || value.startsWith('SPLICE-')) {
   879→        return false;
   880→    }
   881→    // Check if it's legacy base64 encoded JSON
   882→    try {
   883→        const decoded = atob(value);
   884→        const jsonStr = decodeURIComponent(decoded);
   885→        JSON.parse(jsonStr);
   886→        return true;
   887→    } catch {
   888→        return false;
   889→    }
   890→}
   891→
   892→/**
   893→ * Encrypt sensitive fields in settings object
   894→ *
   895→ * @param {Object} settings - Settings object with plaintext sensitive fields
   896→ * @returns {Object} Settings with encrypted sensitive fields
   897→ */
   898→function encryptSensitiveFields(settings) {
   899→    const encrypted = { ...settings };
   900→
   901→    for (const field of SENSITIVE_FIELDS) {
   902→        if (encrypted[field] !== null && encrypted[field] !== undefined) {
   903→            // Only encrypt if not already encrypted
   904→            if (!isEncryptedValue(encrypted[field])) {
   905→                encrypted[field] = encryptForStorage(encrypted[field]);
   906→                debugLog(`[Config] Encrypted field: ${field}`);
   907→            }
   908→        }
   909→    }
   910→
   911→    return encrypted;
   912→}
   913→
   914→/**
   915→ * Decrypt sensitive fields in settings object
   916→ *
   917→ * @param {Object} settings - Settings object with encrypted sensitive fields
   918→ * @returns {Object} Settings with decrypted sensitive fields
   919→ */
   920→function decryptSensitiveFields(settings) {
   921→    const decrypted = { ...settings };
   922→
   923→    for (const field of SENSITIVE_FIELDS) {
   924→        if (decrypted[field] !== null && decrypted[field] !== undefined) {
   925→            // Check if it looks encrypted before attempting to decrypt
   926→            if (isEncryptedValue(decrypted[field])) {
   927→                const decryptedValue = decryptFromStorage(decrypted[field]);
   928→                if (decryptedValue !== null) {
   929→                    decrypted[field] = decryptedValue;
   930→                    debugLog(`[Config] Decrypted field: ${field}`);
   931→                }
   932→            }
   933→            // If not encrypted (legacy data), keep the original value
   934→        }
   935→    }
   936→
   937→    return decrypted;
   938→}
   939→
   940→/**
   941→ * Internal save function to avoid recursion during migration
   942→ * @private
   943→ */
   944→function _saveSettingsInternal(settings) {
   945→    try {
   946→        // Encrypt sensitive fields before storing
   947→        const toStore = encryptSensitiveFields(settings);
   948→
   949→        // Add version marker for future migrations
   950→        toStore._storageVersion = SETTINGS_STORAGE_VERSION;
   951→
   952→        localStorage.setItem('spliceSettings', JSON.stringify(toStore));
   953→        return true;
   954→    } catch (e) {
   955→        console.warn('[Config] Error saving settings:', e);
   956→        return false;
   957→    }
   958→}
   959→
   960→/**
   961→ * Get settings from localStorage
   962→ * Handles migration from unencrypted to encrypted storage format
   963→ */
   964→function getSettings() {
   965→    try {
   966→        const stored = localStorage.getItem('spliceSettings');
   967→        if (stored) {
   968→            const parsed = JSON.parse(stored);
   969→
   970→            // Decrypt sensitive fields (handles both encrypted and legacy unencrypted data)
   971→            const decrypted = decryptSensitiveFields(parsed);
   972→
   973→            // Check if migration is needed (no version marker = legacy data)
   974→            if (!parsed._storageVersion) {
   975→                // Migrate legacy data: save with encryption and version marker
   976→                debugLog('[Config] Migrating legacy settings to encrypted format');
   977→                const migratedSettings = { ...DEFAULT_SETTINGS, ...decrypted };
   978→                // Use internal save to avoid infinite recursion
   979→                _saveSettingsInternal(migratedSettings);
   980→            }
   981→
   982→            // Return merged settings without internal fields
   983→            const result = { ...DEFAULT_SETTINGS, ...decrypted };
   984→            delete result._storageVersion;
   985→            return result;
   986→        }
   987→    } catch (e) {
   988→        console.warn('[Config] Error loading settings:', e);
   989→    }
   990→    return { ...DEFAULT_SETTINGS };
   991→}
   992→
   993→/**
   994→ * Save settings to localStorage
   995→ * Encrypts sensitive fields before storing
   996→ */
   997→function saveSettings(settings) {
   998→    try {
   999→        // Get current settings (decrypted) and merge with new settings
  1000→        const current = getSettings();
  1001→        const merged = { ...current, ...settings };
  1002→
  1003→        return _saveSettingsInternal(merged);
  1004→    } catch (e) {
  1005→        console.warn('[Config] Error saving settings:', e);
  1006→        return false;
  1007→    }
  1008→}
  1009→
  1010→/**
  1011→ * Clear specific setting
  1012→ */
  1013→function clearSetting(key) {
  1014→    const settings = getSettings();
  1015→    delete settings[key];
  1016→    _saveSettingsInternal(settings);
  1017→}
