  1091→/**
  1092→ * SECURITY: Escape HTML to prevent XSS attacks
  1093→ * Use this when inserting untrusted content into innerHTML
  1094→ * @param {string} str - String to escape
  1095→ * @returns {string} Escaped string safe for innerHTML
  1096→ */
  1097→function escapeHtml(str) {
  1098→    if (str === null || str === undefined) return '';
  1099→    if (typeof str !== 'string') str = String(str);
  1100→
  1101→    const escapeMap = {
  1102→        '&': '&amp;',
  1103→        '<': '&lt;',
  1104→        '>': '&gt;',
  1105→        '"': '&quot;',
  1106→        "'": '&#x27;',
  1107→        '/': '&#x2F;',
  1108→        '`': '&#x60;'
  1109→    };
  1110→
  1111→    return str.replace(/[&<>"'\/`]/g, char => escapeMap[char]);
  1112→}
  1113→
  1114→/**
  1115→ * SECURITY: Create safe HTML from template with escaped values
  1116→ * @param {TemplateStringsArray} strings - Template strings
  1117→ * @param {...any} values - Values to escape
  1118→ * @returns {string} HTML with escaped values
  1119→ */
  1120→function safeHtml(strings, ...values) {
  1121→    return strings.reduce((result, str, i) => {
  1122→        const value = values[i - 1];
  1123→        const escaped = escapeHtml(value);
  1124→        return result + escaped + str;
  1125→    });
  1126→}
  1127→
  1128→/**
  1129→ * Format time in MM:SS or HH:MM:SS
  1130→ */
  1131→function formatTime(seconds) {
  1132→    if (isNaN(seconds) || seconds < 0) return '0:00';
  1133→
  1134→    const hours = Math.floor(seconds / 3600);
  1135→    const minutes = Math.floor((seconds % 3600) / 60);
  1136→    const secs = Math.floor(seconds % 60);
  1137→
  1138→    if (hours > 0) {
  1139→        return `${hours}:${minutes.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  1140→    }
  1141→    return `${minutes}:${secs.toString().padStart(2, '0')}`;
  1142→}
  1143→
  1144→/**
  1145→ * Set status message
  1146→ */
  1147→function setStatus(message, isError = false) {
  1148→    const status = document.getElementById('status');
  1149→    if (status) {
  1150→        status.textContent = message;
  1151→        status.style.color = isError ? '#dc3545' : '#888';
  1152→    }
  1153→}
  1154→
  1155→/**
  1156→ * Debounce function calls
  1157→ */
  1158→function debounce(func, wait) {
  1159→    let timeout;
  1160→    return function executedFunction(...args) {
  1161→        const later = () => {
  1162→            clearTimeout(timeout);
  1163→            func(...args);
  1164→        };
  1165→        clearTimeout(timeout);
  1166→        timeout = setTimeout(later, wait);
  1167→    };
  1168→}
