  1426→/**
  1427→ * Initialize login modal handlers
  1428→ */
  1429→function initLoginModal() {
  1430→    const loginModal = document.getElementById('loginModal');
  1431→    const closeLoginBtn = document.getElementById('closeLoginBtn');
  1432→    const saveLoginBtn = document.getElementById('saveLoginBtn');
  1433→    const licenseKeyInput = document.getElementById('licenseKeyInput');
  1434→    const creditBadge = document.getElementById('creditBadge');
  1435→
  1436→    // Handle credit badge click - retry on error, or show login modal
  1437→    if (creditBadge) {
  1438→        creditBadge.addEventListener('click', async () => {
  1439→            if (creditBadge.classList.contains('error')) {
  1440→                creditBadge.textContent = '...';
  1441→                if (typeof refreshCredits === 'function') {
  1442→                    await refreshCredits();
  1443→                }
  1444→                return;
  1445→            }
  1446→            showLoginModal();
  1447→        });
  1448→    }
  1449→
  1450→    // Close login modal
  1451→    if (closeLoginBtn && loginModal) {
  1452→        closeLoginBtn.addEventListener('click', () => {
  1453→            loginModal.classList.add('hidden');
  1454→        });
  1455→    }
  1456→
  1457→    // Close on backdrop click
  1458→    if (loginModal) {
  1459→        loginModal.addEventListener('click', (e) => {
  1460→            if (e.target === loginModal) {
  1461→                loginModal.classList.add('hidden');
  1462→            }
  1463→        });
  1464→    }
  1465→
  1466→    // Activate license key
  1467→    if (saveLoginBtn) {
  1468→        saveLoginBtn.addEventListener('click', async () => {
  1469→            const licenseKey = licenseKeyInput?.value?.trim()?.toUpperCase();
  1470→
  1471→            if (!licenseKey) {
  1472→                showLoginError('Please enter your license key');
  1473→                return;
  1474→            }
  1475→
  1476→            if (!isValidLicenseKeyFormat(licenseKey)) {
  1477→                showLoginError('Invalid format. Expected: SPLICE-XXXX-XXXX-XXXX');
  1478→                return;
  1479→            }
  1480→
  1481→            saveLoginBtn.disabled = true;
  1482→            saveLoginBtn.textContent = 'Activating...';
  1483→
  1484→            try {
  1485→                // Use /auth/login endpoint which returns JWT tokens
  1486→                const response = await fetchWithTimeout(`${getBackendUrl()}/auth/login`, {
  1487→                    method: 'POST',
  1488→                    headers: { 'Content-Type': 'application/json' },
  1489→                    body: JSON.stringify({ licenseKey: licenseKey })
  1490→                }, SPLICE_CONFIG.FETCH_TIMEOUT);
  1491→
  1492→                const result = await response.json();
  1493→
  1494→                if (!result.success) {
  1495→                    showLoginError(result.error || 'Activation failed');
  1496→                    return;
  1497→                }
  1498→
  1499→                // Store JWT tokens and customer info
  1500→                const expiresInMs = (result.expiresIn || 86400) * 1000; // Default 24h
  1501→                saveSettings({
  1502→                    customerId: result.customerId,
  1503→                    accessToken: result.token,
  1504→                    refreshToken: result.refreshToken,
  1505→                    tokenExpiry: Date.now() + expiresInMs,
  1506→                    licenseKey: licenseKey // Keep for re-authentication
  1507→                });
  1508→
  1509→                loginModal?.classList.add('hidden');
  1510→
  1511→                if (typeof refreshCredits === 'function') {
  1512→                    await refreshCredits();
  1513→                }
  1514→
  1515→                const tierName = result.tier ? result.tier.charAt(0).toUpperCase() + result.tier.slice(1) : 'Unknown';
  1516→                setStatus(`License activated! ${tierName} tier`);
  1517→            } catch (err) {
  1518→                console.error('[SPLICE] License activation error:', err);
  1519→
  1520→                // DIAGNOSTIC LOGGING: Capture exact error details (debug mode only)
  1521→                debugLog('=== CEP LICENSE ACTIVATION ERROR DIAGNOSTICS ===');
  1522→                debugLog('Error name:', err?.name);
  1523→                debugLog('Error message:', err?.message);
  1524→                debugLog('Error stack:', err?.stack);
  1525→                debugLog('Backend URL:', getBackendUrl());
  1526→                debugLog('Fetch timeout:', SPLICE_CONFIG.FETCH_TIMEOUT);
  1527→                debugLog('Is CEP environment:', typeof CSInterface !== 'undefined');
  1528→                debugLog('User agent:', navigator?.userAgent);
  1529→                debugLog('Error constructor:', err?.constructor?.name);
  1530→                debugLog('Is TypeError:', err?.name === 'TypeError');
  1531→                debugLog('Message contains "Failed to fetch":', err?.message?.includes('Failed to fetch'));
  1532→                debugLog('Message contains "fetch":', err?.message?.toLowerCase()?.includes('fetch'));
  1533→                debugLog('Message contains "network":', err?.message?.toLowerCase()?.includes('network'));
  1534→                debugLog('Message contains "CORS":', err?.message?.toLowerCase()?.includes('cors'));
  1535→                debugLog('Message contains "SSL":', err?.message?.toLowerCase()?.includes('ssl'));
  1536→                debugLog('Message contains "certificate":', err?.message?.toLowerCase()?.includes('certificate'));
  1537→                debugLog('Full error object:', JSON.stringify(err, Object.getOwnPropertyNames(err)));
  1538→                debugLog('================================================');
  1539→
  1540→                // Provide detailed error message based on error type
  1541→                let errorMessage = 'Connection error. ';
  1542→
  1543→                if (err.message === 'Request timed out') {
  1544→                    errorMessage += 'Server is not responding. Check if backend is running.';
  1545→                } else if (err.message?.includes('Failed to fetch') || err.name === 'TypeError') {
  1546→                    errorMessage += `Cannot reach ${getBackendUrl()}. Check network/firewall.`;
  1547→                } else if (err.message?.includes('CORS')) {
  1548→                    errorMessage += 'CORS error. Contact support.';
  1549→                } else if (err.message?.includes('SSL') || err.message?.includes('certificate')) {
  1550→                    errorMessage += 'SSL certificate error. Try updating your system certificates.';
  1551→                } else if (err.message) {
  1552→                    errorMessage += err.message;
  1553→                } else {
  1554→                    errorMessage += 'Unknown error. Check console for details.';
  1555→                }
  1556→
  1557→                debugLog('Final error message shown to user:', errorMessage);
  1558→                showLoginError(errorMessage);
  1559→            } finally {
  1560→                saveLoginBtn.disabled = false;
  1561→                saveLoginBtn.textContent = 'Activate';
  1562→            }
  1563→        });
  1564→    }
  1565→
  1566→    // Email lookup
  1567→    const lookupLicenseBtn = document.getElementById('lookupLicenseBtn');
  1568→    const lookupEmailInput = document.getElementById('lookupEmailInput');
  1569→
  1570→    if (lookupLicenseBtn) {
  1571→        lookupLicenseBtn.addEventListener('click', async () => {
  1572→            const email = lookupEmailInput?.value?.trim()?.toLowerCase();
  1573→
  1574→            if (!email) {
  1575→                showLoginError('Please enter your email address');
  1576→                return;
  1577→            }
  1578→
  1579→            const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  1580→            if (!emailRegex.test(email)) {
  1581→                showLoginError('Please enter a valid email address');
  1582→                return;
  1583→            }
  1584→
  1585→            lookupLicenseBtn.disabled = true;
  1586→            lookupLicenseBtn.textContent = 'Looking up...';
  1587→
  1588→            try {
  1589→                const response = await fetchWithTimeout(`${getBackendUrl()}/license/lookup`, {
  1590→                    method: 'POST',
  1591→                    headers: { 'Content-Type': 'application/json' },
  1592→                    body: JSON.stringify({ email })
  1593→                }, 30000);
  1594→
  1595→                const result = await response.json();
  1596→
  1597→                // Note: For security, the backend does NOT return the license key directly.
  1598→                // It sends the key via email to prevent enumeration attacks.
  1599→                if (result.success) {
  1600→                    setStatus(result.message || 'If a license exists, it will be sent to your email.');
  1601→                } else {
  1602→                    showLoginError(result.error || 'Lookup failed. Please try again.');
  1603→                }
  1604→            } catch (err) {
  1605→                console.error('[SPLICE] License lookup error:', err);
  1606→                showLoginError('Failed to look up license. Please try again.');
  1607→            } finally {
  1608→                lookupLicenseBtn.disabled = false;
  1609→                lookupLicenseBtn.textContent = 'Lookup';
  1610→            }
  1611→        });
  1612→    }
  1613→
  1614→    // Handle external URL buttons
  1615→    // CEP panels cannot use target="_blank" for security reasons.
  1616→    // Must use CSInterface.openURLInDefaultBrowser() instead.
  1617→    initExternalLinkButtons();
  1618→}
