  1741→// ============================================================================
  1742→// CENTRALIZED ERROR LOGGING (FIX: CEP-ERR-007)
  1743→// Captures errors for debugging across different Mac configurations
  1744→// ============================================================================
  1745→
  1746→/**
  1747→ * Error log storage - persisted to localStorage for debugging
  1748→ * Stores the last 50 errors with timestamps and context
  1749→ */
  1750→const ERROR_LOG_KEY = 'spliceErrorLog';
  1751→const MAX_ERROR_LOG_SIZE = 50;
  1752→
  1753→/**
  1754→ * Log an error with context for debugging
  1755→ * FIX: CEP-ERR-007 - Centralized error capture for debugging crashes
  1756→ *
  1757→ * @param {string} module - Module name (e.g., 'JSX', 'Silence', 'Takes')
  1758→ * @param {string} operation - Operation that failed
  1759→ * @param {Error|string} error - Error object or message
  1760→ * @param {Object} context - Additional context for debugging
  1761→ */
  1762→function logError(module, operation, error, context = {}) {
  1763→    const errorEntry = {
  1764→        timestamp: new Date().toISOString(),
  1765→        module,
  1766→        operation,
  1767→        message: error?.message || String(error),
  1768→        stack: error?.stack || null,
  1769→        code: error?.code || null,
  1770→        context: {
  1771→            ...context,
  1772→            userAgent: navigator?.userAgent,
  1773→            isCEP: typeof window.__adobe_cep__ !== 'undefined',
  1774→            isOnline: navigator?.onLine
  1775→        }
  1776→    };
  1777→
  1778→    // Log to console
  1779→    console.error(`[SPLICE ${module}] ${operation}:`, error);
  1780→    if (SPLICE_DEBUG) {
  1781→        console.error('[SPLICE ERROR CONTEXT]', errorEntry);
  1782→    }
  1783→
  1784→    // Persist to localStorage for later debugging
  1785→    try {
  1786→        let errorLog = [];
  1787→        const stored = localStorage.getItem(ERROR_LOG_KEY);
  1788→        if (stored) {
  1789→            errorLog = JSON.parse(stored);
  1790→        }
  1791→
  1792→        // Add new error and trim to max size
  1793→        errorLog.unshift(errorEntry);
  1794→        if (errorLog.length > MAX_ERROR_LOG_SIZE) {
  1795→            errorLog = errorLog.slice(0, MAX_ERROR_LOG_SIZE);
  1796→        }
  1797→
  1798→        localStorage.setItem(ERROR_LOG_KEY, JSON.stringify(errorLog));
  1799→    } catch (e) {
  1800→        // Silent fail - localStorage might be full or unavailable
  1801→        console.warn('[SPLICE] Failed to persist error log:', e);
  1802→    }
  1803→
  1804→    return errorEntry;
  1805→}
  1806→
  1807→/**
  1808→ * Get the stored error log for debugging
  1809→ * @returns {Array} Array of error entries
  1810→ */
  1811→function getErrorLog() {
  1812→    try {
  1813→        const stored = localStorage.getItem(ERROR_LOG_KEY);
  1814→        return stored ? JSON.parse(stored) : [];
  1815→    } catch (e) {
  1816→        return [];
  1817→    }
  1818→}
  1819→
  1820→/**
  1821→ * Clear the error log
  1822→ */
  1823→function clearErrorLog() {
  1824→    try {
  1825→        localStorage.removeItem(ERROR_LOG_KEY);
  1826→    } catch (e) {
  1827→        // Silent fail
  1828→    }
  1829→}
  1830→
  1831→/**
  1832→ * Format error log for display or copy
  1833→ * @returns {string} Formatted error log text
  1834→ */
  1835→function formatErrorLog() {
  1836→    const log = getErrorLog();
  1837→    if (log.length === 0) {
  1838→        return 'No errors logged.';
  1839→    }
  1840→
  1841→    return log.map((entry, i) => {
  1842→        return [
