   440→function deleteClipsAtSilencePointsInTrack(silences, trackType, trackIndex) {
   441→    try {
   442→        silences = JSON.parse(silences);
   443→    } catch (e) {
   444→        return JSON.stringify({ error: "Invalid JSON input: " + e.message });
   445→    }
   446→    var clips;
   447→    if (trackType == "video") {
   448→        clips = app.project.activeSequence.videoTracks[trackIndex].clips;
   449→    } else if (trackType == "audio") {
   450→        clips = app.project.activeSequence.audioTracks[trackIndex].clips;
   451→    }
   452→
   453→    var deleted = 0;
   454→    for (var i = clips.length - 1; i >= 0; i--) {
   455→        var clip = clips[i];
   456→        var start = clip.start.seconds;
   457→        var end = clip.end.seconds;
   458→        var center = (start + end) / 2;
   459→
   460→        for (var j = 0; j < silences.length; j++) {
   461→            var silence = silences[j];
   462→            var silenceCenter = (silence[0] + silence[1]) / 2;
   463→            if (silenceCenter > start && silenceCenter < end) {
   464→                clip.remove(false, false);
   465→                deleted++;
   466→                break;
   467→            }
   468→        }
   469→    }
   470→    return JSON.stringify({ deleted: deleted });
   471→}
