  1270→ * Used as fallback when AME export is not available
  1271→ * @returns {Object} { success, path } or { error }
  1272→ */
  1273→function getFirstClipAudioPath() {
  1274→    try {
  1275→        var seq = app.project.activeSequence;
  1276→        if (!seq) return JSON.stringify({ error: "No active sequence" });
  1277→
  1278→        // First try audio tracks
  1279→        for (var i = 0; i < seq.audioTracks.numTracks; i++) {
  1280→            var track = seq.audioTracks[i];
  1281→            if (track.clips.numItems > 0) {
  1282→                var clip = track.clips[0];
  1283→                if (clip.projectItem && clip.projectItem.getMediaPath) {
  1284→                    var mediaPath = clip.projectItem.getMediaPath();
  1285→                    if (mediaPath && mediaPath.length > 0) {
  1286→                        return JSON.stringify({ success: true, path: mediaPath, method: "audio_track" });
  1287→                    }
  1288→                }
  1289→            }
  1290→        }
  1291→
  1292→        // Fall back to video tracks (which may have linked audio)
  1293→        for (var i = 0; i < seq.videoTracks.numTracks; i++) {
  1294→            var track = seq.videoTracks[i];
  1295→            if (track.clips.numItems > 0) {
  1296→                var clip = track.clips[0];
  1297→                if (clip.projectItem && clip.projectItem.getMediaPath) {
  1298→                    var mediaPath = clip.projectItem.getMediaPath();
  1299→                    if (mediaPath && mediaPath.length > 0) {
  1300→                        return JSON.stringify({ success: true, path: mediaPath, method: "video_track" });
  1301→                    }
  1302→                }
  1303→            }
  1304→        }
  1305→
  1306→        return JSON.stringify({ error: "No clips with media paths found in sequence" });
  1307→    } catch (e) {
