  1316→function moveClipByNTracks(currentTrackId, clipStartTime, nTracksToMove, trackType) {
  1317→    if (!isQEAvailable() && !enableQE()) {
  1318→        return JSON.stringify({ error: "QE not available for moving clips between tracks" });
  1319→    }
  1320→
  1321→    try {
  1322→        var accuracy = 3;
  1323→        var search_start_time = clipStartTime.toFixed(accuracy);
  1324→
  1325→        if (trackType == "audio") {
  1326→            var numClipsInTrack = qe.project.getActiveSequence().getAudioTrackAt(currentTrackId).numItems;
  1327→            for (var j = 0; j < numClipsInTrack; j++) {
  1328→                if (qe.project.getActiveSequence().getAudioTrackAt(currentTrackId).getItemAt(j).start.secs.toFixed(accuracy) == search_start_time) {
  1329→                    qe.project.getActiveSequence().getAudioTrackAt(currentTrackId).getItemAt(j).moveToTrack(0, nTracksToMove, "0");
  1330→                    return JSON.stringify({ success: true });
  1331→                }
  1332→            }
  1333→        } else if (trackType == "video") {
  1334→            var numClipsInTrack = qe.project.getActiveSequence().getVideoTrackAt(currentTrackId).numItems;
  1335→            for (var j = 0; j < numClipsInTrack; j++) {
  1336→                if (qe.project.getActiveSequence().getVideoTrackAt(currentTrackId).getItemAt(j).start.secs.toFixed(accuracy) == search_start_time) {
  1337→                    qe.project.getActiveSequence().getVideoTrackAt(currentTrackId).getItemAt(j).moveToTrack(nTracksToMove, 0, "0");
  1338→                    return JSON.stringify({ success: true });
  1339→                }
  1340→            }
  1341→        }
  1342→        return JSON.stringify({ error: "Clip not found at specified time" });
  1343→    } catch (e) {
  1344→        return JSON.stringify({ error: "Move clip failed: " + e.message });
  1345→    }
  1346→}
  1347→
  1348→function linkIdenticalClipsInTracks(videoTrackIndex, audioTrackIndex) {
  1349→    var selected_clips = app.project.activeSequence.getSelection();
  1350→    for (var i = 0; i < selected_clips.length; i++) {
  1351→        selected_clips[i].setSelected(false, false);
  1352→    }
  1353→
  1354→    var video_clips = app.project.activeSequence.videoTracks[videoTrackIndex].clips;
  1355→    var audio_clips = app.project.activeSequence.audioTracks[audioTrackIndex].clips;
  1356→
  1357→    for (var i = 0; i < video_clips.numItems; i++) {
  1358→        var video_clip = video_clips[i];
  1359→        var video_start = video_clip.start.seconds;
  1360→
  1361→        for (var j = 0; j < audio_clips.numItems; j++) {
  1362→            var audio_clip = audio_clips[j];
  1363→            var audio_start = audio_clip.start.seconds;
  1364→
  1365→            if (Math.abs(video_start - audio_start) < time_tolerance) {
  1366→                video_clip.setSelected(true, false);
  1367→                audio_clip.setSelected(true, false);
  1368→                app.project.activeSequence.linkSelection();
  1369→                video_clip.setSelected(false, false);
  1370→                audio_clip.setSelected(false, false);
  1371→                break;
  1372→            }
  1373→        }
  1374→    }
  1375→    return true;
