  1309→/**
  1310→ * Apply a preset to current settings
  1311→ * @param {string} presetName - Name of the preset to apply
  1312→ * @returns {Object} The applied settings
  1313→ */
  1314→function applyPreset(presetName) {
  1315→    const preset = PRESETS[presetName];
  1316→
  1317→    if (!preset) {
  1318→        console.warn(`[SPLICE] Unknown preset: ${presetName}`);
  1319→        return getSettings();
  1320→    }
  1321→
  1322→    // Custom preset uses current settings
  1323→    if (presetName === 'custom' || !preset.settings) {
  1324→        saveSettings({ activePreset: 'custom' });
  1325→        return getSettings();
  1326→    }
  1327→
  1328→    // Apply preset settings
  1329→    const newSettings = {
  1330→        ...preset.settings,
  1331→        activePreset: presetName
  1332→    };
  1333→
  1334→    saveSettings(newSettings);
  1335→    debugLog(`Applied preset: ${preset.name}`);
  1336→
  1337→    return getSettings();
  1338→}
