  1091/**
  1092 * SECURITY: Escape HTML to prevent XSS attacks
  1093 * Use this when inserting untrusted content into innerHTML
  1094 * @param {string} str - String to escape
  1095 * @returns {string} Escaped string safe for innerHTML
  1096 */
  1097function escapeHtml(str) {
  1098    if (str === null || str === undefined) return '';
  1099    if (typeof str !== 'string') str = String(str);
  1100
  1101    const escapeMap = {
  1102        '&': '&amp;',
  1103        '<': '&lt;',
  1104        '>': '&gt;',
  1105        '"': '&quot;',
  1106        "'": '&#x27;',
  1107        '/': '&#x2F;',
  1108        '`': '&#x60;'
  1109    };
  1110
  1111    return str.replace(/[&<>"'\/`]/g, char => escapeMap[char]);
  1112}
  1113
  1114/**
  1115 * SECURITY: Create safe HTML from template with escaped values
  1116 * @param {TemplateStringsArray} strings - Template strings
  1117 * @param {...any} values - Values to escape
  1118 * @returns {string} HTML with escaped values
  1119 */
  1120function safeHtml(strings, ...values) {
  1121    return strings.reduce((result, str, i) => {
  1122        const value = values[i - 1];
  1123        const escaped = escapeHtml(value);
  1124        return result + escaped + str;
  1125    });
  1126}
