  1415/**
  1416 * Validate license key format: SPLICE-XXXX-XXXX-XXXX
  1417 * Character set excludes confusing chars: 0/O, 1/I/L
  1418 */
  1419function isValidLicenseKeyFormat(key) {
  1420    if (!key || typeof key !== 'string') return false;
  1421    const normalized = key.toUpperCase().trim();
  1422    const pattern = /^SPLICE-[ABCDEFGHJKMNPQRSTUVWXYZ23456789]{4}-[ABCDEFGHJKMNPQRSTUVWXYZ23456789]{4}-[ABCDEFGHJKMNPQRSTUVWXYZ23456789]{4}$/;
  1423    return pattern.test(normalized);
  1424}
