  1632    try {
  1633        cs = new CSInterface();
  1634    } catch (e) {
  1635        console.warn('[SPLICE] Failed to create CSInterface:', e.message);
  1636        return;
  1637    }
  1638
  1639    // Buy a License button
  1640    const buyLicenseBtn = document.querySelector('.btn-buy-license');
  1641
  1642    if (buyLicenseBtn) {
  1643        buyLicenseBtn.addEventListener('click', (e) => {
  1644            e.preventDefault();
  1645            e.stopPropagation();
  1646
  1647            // Visual feedback
  1648            buyLicenseBtn.textContent = 'Opening...';
  1649            buyLicenseBtn.style.backgroundColor = '#28a745';
  1650
  1651            try {
  1652                cs.openURLInDefaultBrowser('https://spliceclips.com/pricing');
  1653
  1654                // Reset button after a delay
  1655                setTimeout(() => {
  1656                    buyLicenseBtn.textContent = 'Buy License';
  1657                    buyLicenseBtn.style.backgroundColor = '';
  1658                }, 2000);
  1659            } catch (err) {
  1660                console.error('[SPLICE] Failed to open URL:', err.message);
  1661                buyLicenseBtn.textContent = 'Error';
  1662                buyLicenseBtn.style.backgroundColor = '#dc3545';
  1663            }
  1664        });
  1665    }
  1666
  1667    // View Plans button (in upgrade modal)
  1668    const viewPlansBtn = document.querySelector('.btn-view-plans');
  1669    if (viewPlansBtn) {
  1670        viewPlansBtn.addEventListener('click', (e) => {
  1671            e.preventDefault();
