  1689
  1690/**
  1691 * Show login modal
  1692 */
  1693function showLoginModal() {
  1694    const loginModal = document.getElementById('loginModal');
  1695    const loginError = document.getElementById('loginError');
  1696    if (loginError) loginError.style.display = 'none';
  1697    if (loginModal) loginModal.classList.remove('hidden');
  1698}
  1699
  1700/**
  1701 * Show login error message
  1702 */
  1703function showLoginError(message) {
  1704    const loginError = document.getElementById('loginError');
  1705    if (loginError) {
  1706        loginError.textContent = message;
  1707        loginError.style.display = 'block';
  1708    }
  1709}
  1710
  1711/**
  1712 * Check if user is logged in
  1713 */
  1714function isLoggedIn() {
  1715    const settings = getSettings();
  1716    return !!settings.customerId;
  1717}
  1718
  1719/**
  1720 * Logout - clear all auth data
  1721 */
  1722function logout() {
  1723    // Clear all auth-related settings
  1724    saveSettings({
  1725        customerId: null,
  1726        accessToken: null,
  1727        refreshToken: null,
  1728        tokenExpiry: null,
  1729        licenseKey: null
  1730    });
  1731
  1732    if (typeof clearCreditsCache === 'function') {
  1733        clearCreditsCache();
  1734    }
  1735    if (typeof updateCreditDisplay === 'function') {
  1736        updateCreditDisplay(null);
  1737    }
  1738    setStatus('Logged out');
  1739}
  1740
  1741// ============================================================================
  1742// CENTRALIZED ERROR LOGGING (FIX: CEP-ERR-007)
  1743// Captures errors for debugging across different Mac configurations
  1744// ============================================================================
  1745
  1746/**
  1747 * Error log storage - persisted to localStorage for debugging
  1748 * Stores the last 50 errors with timestamps and context
  1749 */
  1750const ERROR_LOG_KEY = 'spliceErrorLog';
  1751const MAX_ERROR_LOG_SIZE = 50;
  1752
  1753/**
  1754 * Log an error with context for debugging
  1755 * FIX: CEP-ERR-007 - Centralized error capture for debugging crashes
  1756 *
  1757 * @param {string} module - Module name (e.g., 'JSX', 'Silence', 'Takes')
  1758 * @param {string} operation - Operation that failed
  1759 * @param {Error|string} error - Error object or message
  1760 * @param {Object} context - Additional context for debugging
  1761 */
  1762function logError(module, operation, error, context = {}) {
  1763    const errorEntry = {
  1764        timestamp: new Date().toISOString(),
  1765        module,
  1766        operation,
  1767        message: error?.message || String(error),
  1768        stack: error?.stack || null,
  1769        code: error?.code || null,
  1770        context: {
  1771            ...context,
  1772            userAgent: navigator?.userAgent,
  1773            isCEP: typeof window.__adobe_cep__ !== 'undefined',
  1774            isOnline: navigator?.onLine
  1775        }
  1776    };
  1777
  1778    // Log to console
  1779    console.error(`[SPLICE ${module}] ${operation}:`, error);
  1780    if (SPLICE_DEBUG) {
  1781        console.error('[SPLICE ERROR CONTEXT]', errorEntry);
  1782    }
  1783
  1784    // Persist to localStorage for later debugging
  1785    try {
  1786        let errorLog = [];
  1787        const stored = localStorage.getItem(ERROR_LOG_KEY);
  1788        if (stored) {
  1789            errorLog = JSON.parse(stored);
  1790        }
  1791
  1792        // Add new error and trim to max size
  1793        errorLog.unshift(errorEntry);
  1794        if (errorLog.length > MAX_ERROR_LOG_SIZE) {
  1795            errorLog = errorLog.slice(0, MAX_ERROR_LOG_SIZE);
  1796        }
  1797
  1798        localStorage.setItem(ERROR_LOG_KEY, JSON.stringify(errorLog));
  1799    } catch (e) {
  1800        // Silent fail - localStorage might be full or unavailable
  1801        console.warn('[SPLICE] Failed to persist error log:', e);
  1802    }
  1803
  1804    return errorEntry;
  1805}
  1806
  1807/**
  1808 * Get the stored error log for debugging
  1809 * @returns {Array} Array of error entries
  1810 */
  1811function getErrorLog() {
  1812    try {
  1813        const stored = localStorage.getItem(ERROR_LOG_KEY);
  1814        return stored ? JSON.parse(stored) : [];
  1815    } catch (e) {
  1816        return [];
  1817    }
  1818}
  1819
  1820/**
  1821 * Clear the error log
  1822 */
  1823function clearErrorLog() {
  1824    try {
  1825        localStorage.removeItem(ERROR_LOG_KEY);
  1826    } catch (e) {
  1827        // Silent fail
  1828    }
  1829}
  1830
  1831/**
  1832 * Format error log for display or copy
  1833 * @returns {string} Formatted error log text
  1834 */
  1835function formatErrorLog() {
  1836    const log = getErrorLog();
  1837    if (log.length === 0) {
  1838        return 'No errors logged.';
  1839    }
  1840
  1841    return log.map((entry, i) => {
  1842        return [
  1843            `--- Error ${i + 1} ---`,
  1844            `Time: ${entry.timestamp}`,
  1845            `Module: ${entry.module}`,
  1846            `Operation: ${entry.operation}`,
  1847            `Message: ${entry.message}`,
  1848            entry.code ? `Code: ${entry.code}` : null,
  1849            entry.stack ? `Stack: ${entry.stack}` : null,
  1850            `CEP: ${entry.context?.isCEP ? 'Yes' : 'No'}`,
  1851            `Online: ${entry.context?.isOnline ? 'Yes' : 'No'}`,
  1852            ''
  1853        ].filter(Boolean).join('\n');
  1854    }).join('\n');
  1855}
  1856
  1857// ============================================================================
  1858// GLOBAL ERROR HANDLERS (FIX: CEP-ERR-008)
  1859// Catch unhandled errors and promise rejections
  1860// ============================================================================
  1861
  1862/**
  1863 * Global error handler for uncaught exceptions
  1864 */
  1865window.addEventListener('error', (event) => {
  1866    logError('Global', 'Uncaught Exception', event.error || event.message, {
  1867        filename: event.filename,
  1868        lineno: event.lineno,
  1869        colno: event.colno
  1870    });
  1871});
  1872
  1873/**
  1874 * Global handler for unhandled promise rejections
  1875 */
  1876window.addEventListener('unhandledrejection', (event) => {
  1877    logError('Global', 'Unhandled Promise Rejection', event.reason, {
  1878        promise: 'rejected'
  1879    });
  1880});
  1881
  1882// Initialize JSX bridge when loaded
  1883document.addEventListener('DOMContentLoaded', () => {
  1884    jsx.init();
  1885});
  1886
  1887// Export for modules
  1888window.SPLICE_CONFIG = SPLICE_CONFIG;
  1889window.SPLICE_DEBUG = SPLICE_DEBUG;
  1890window.JSX_TIMEOUTS = JSX_TIMEOUTS;  // FIX: CEP-CRIT-001 - Export timeout constants
  1891window.debugLog = debugLog;
  1892window.debugFetch = debugFetch;
  1893window.getBackendUrl = getBackendUrl;
  1894window.fetchWithTimeout = fetchWithTimeout;
  1895window.parseErrorResponse = parseErrorResponse;
  1896window.getAuthHeaders = getAuthHeaders;
  1897window.getSettings = getSettings;
  1898window.saveSettings = saveSettings;
  1899window.clearSetting = clearSetting;
  1900window.isOnline = isOnline;
  1901window.initOfflineDetection = initOfflineDetection;
  1902window.formatTime = formatTime;
  1903window.setStatus = setStatus;
  1904window.debounce = debounce;
  1905window.jsx = jsx;
  1906window.PRESETS = PRESETS;
  1907window.getPresets = getPresets;
  1908window.getPreset = getPreset;
  1909window.applyPreset = applyPreset;
  1910window.getActivePreset = getActivePreset;
  1911window.isBuiltInPreset = isBuiltInPreset;
  1912window.initPresetSelector = initPresetSelector;
  1913window.initLoginModal = initLoginModal;
  1914window.showLoginModal = showLoginModal;
  1915window.isLoggedIn = isLoggedIn;
  1916window.logout = logout;
  1917window.escapeHtml = escapeHtml;
  1918window.safeHtml = safeHtml;
  1919// JWT Auth functions
  1920window.isTokenExpired = isTokenExpired;
  1921window.refreshAccessToken = refreshAccessToken;
  1922window.clearAuthTokens = clearAuthTokens;
  1923window.ensureValidToken = ensureValidToken;
  1924// Error logging functions (FIX: CEP-ERR-007)
