   387â†’            console.log('[SPLICE] checkSequenceOpen: result =', result, '(type:', typeof result + ')');
   388â†’            debugLog('checkSequenceOpen attempt', attempt + 1, 'result:', result, 'type:', typeof result);
   389â†’
   390â†’            if (result === true || result === 'true') {
   391â†’                console.log('[SPLICE] checkSequenceOpen: Returning TRUE - sequence is open');
   392â†’                console.log('[SPLICE] ========== checkSequenceOpen DIAGNOSTIC END (TRUE) ==========');
   393â†’                return true;
   394â†’            }
   395â†’
   396â†’            // If false, wait and retry (sequence might be loading)
   397â†’            if (attempt < retries) {
   398â†’                console.log('[SPLICE] checkSequenceOpen: No sequence found, retrying in', delayMs, 'ms...');
   399â†’                debugLog('checkSequenceOpen: No sequence found, retrying in', delayMs, 'ms...');
   400â†’                await new Promise(r => setTimeout(r, delayMs));
   401â†’            }
   402â†’        } catch (error) {
   403â†’            console.error('[SPLICE] checkSequenceOpen: Attempt', attempt + 1, 'failed with exception:', error.message);
   404â†’            console.error('[SPLICE] checkSequenceOpen: Error stack:', error.stack);
   405â†’
   406â†’            // On last attempt, return false but log the actual error
   407â†’            if (attempt === retries) {
   408â†’                console.error('[SPLICE] checkSequenceOpen: All attempts failed. Last error:', error.message);
   409â†’                // Store error for diagnostics
   410â†’                window.spliceState.lastSequenceCheckError = error.message;
   411â†’                console.log('[SPLICE] checkSequenceOpen: Stored error in window.spliceState.lastSequenceCheckError:', error.message);
   412â†’                console.log('[SPLICE] ========== checkSequenceOpen DIAGNOSTIC END (ERROR) ==========');
   413â†’                return false;
   414â†’            }
   415â†’
   416â†’            // Wait before retry
   417â†’            console.log('[SPLICE] checkSequenceOpen: Waiting', delayMs, 'ms before retry...');
   418â†’            await new Promise(r => setTimeout(r, delayMs));
   419â†’        }
   420â†’    }
   421â†’    console.log('[SPLICE] checkSequenceOpen: All attempts completed without finding sequence');
   422â†’    console.log('[SPLICE] ========== checkSequenceOpen DIAGNOSTIC END (FALSE) ==========');
   423â†’    return false;
   424â†’}
