   638â†’
   639â†’    // Custom preset - keep current settings
   640â†’    if (presetId === 'custom' || !preset.settings) {
   641â†’        saveSettings({ activePreset: 'custom' });
   642â†’        setStatus('Using custom settings');
   643â†’        return;
   644â†’    }
   645â†’
   646â†’    const settings = preset.settings;
   647â†’
   648â†’    // Apply to UI
   649â†’    if (ui.sensitivitySlider && settings.sensitivity !== undefined) {
   650â†’        ui.sensitivitySlider.value = settings.sensitivity;
   651â†’        if (ui.sensitivityValue) ui.sensitivityValue.textContent = settings.sensitivity;
